css:
@-moz-document domain("twitter.com") {
/*Expand Images - installkey expandimages*/

/*Image*/
div[data-testid="primaryColumn"] article div[data-testid="tweet"] 
a[href$="/photo/1"]:only-child 
img {
  position: relative;
}

div[data-testid="primaryColumn"] article div[data-testid="tweet"] 
a[href$="/photo/1"]:only-child >
div:first-child >
div.r-u8s1d:nth-child(2) {
    position:relative;
}

/*Remove Margin*/
div[data-testid="primaryColumn"] article div[data-testid="tweet"] 
a[href$="/photo/1"]:only-child >
div:first-child >
div[style]:first-child {
    display:none;
}

div[data-testid="primaryColumn"] article div[data-testid="tweet"] 
a[href$="/photo/1"]:only-child >
div:first-child >
div:nth-child(2) >
div.r-u8s1d:first-child {
    position:relative;
}
}
