css:
@-moz-document domain("lichess.org")
{
  square.last-move
  {
    will-change: transform;
    background-color: rgba(50,200,255,0.4);
  }

  square.move-dest
  {
    background: radial-gradient(rgba(50,0,0,0.5) 19%, rgba(100,0,0,0) 20%);
    pointer-events: auto;
  }
  
    square.oc.move-dest
  {
    background: radial-gradient(transparent 50%, transparent 50%, rgba(0,0,0,0) 50%);
  }

  square.selected
  {
    background-color: rgba(50,200,255,0.4);
  }
}
