css:
@-moz-document domain("discord.com") {
div[class*=' blockedSystemMessage'],
div[class^=blockedSystemMessage] {
    display: none
}
div[class*=' wrapper'],
div[class^=wrapper] {
    min-height: 0!important;
}
div[class*=' scrollerSpacer'],
div[class^=scrollerSpacer] {
    height: 0!important
}
div[class*=' scrollerWrap'],
div[class^=scrollerWrap] {
    margin-bottom: 25px
}


div[class*='cozy'],
div[class^=cozy] {
    padding-top: 0 !important;
    padding-bottom: 0 !important;;

}

}
