css:
@-moz-document domain("urania.edu.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #303c4d #1c1c1c;
}
a,h1,h2,h3,h4{
    color: #fff !important;
}
p,span{
    color: #fff !important;
}
a:hover{
    text-decoration: underline !important;
}
*{
    box-shadow: none !important;
	text-shadow: none !important;
}
input[type="text"],input[type="password"],input[type="email"],input[type="search"],input[type="submit"],input[type="tel"],input[type="url"],textarea,select{
    background: #1c1c1c !important;
    color: #fff !important;
    border: 1px solid #303c4d !important;
}
input[type="submit"]:hover{
    background: #303c4d !important;
    color: #fff !important;
}
/*HEADER*/
header.header-v1,.menu-hover .navigation .gva_menu .sub-menu,.menu-hover .navigation .gva_menu .sub-menu > li ul{
    background: #1c1c1c;
}
header a.site-branding-logo img{
    filter: invert();
}
.topbar .topbar-right .social-list a{
    color: #fff !important;
}
.menu-hover .navigation .gva_menu .sub-menu > li{
    border-color: #fff;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.promotion,.main.main-page,body div.body-page{
    background: #1c1c1c;
}
.node--view-mode-slider-3 .post-content,.node--view-mode-teaser-3.node-index-1 .post-content{
    background: #1c1c1c;
    border: 1px solid #303c4d;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.node--view-mode-full .post-tags a{
    border: 1px solid #303c4d;
}
.node--view-mode-full .post-tags a:hover{
    background: #303c4d;
}
/*ARTYKUŁ*/
/*PODSTRONY*/
.pager li.is-active > a,.pager li > a:hover, .pager li > a:focus{
    background: #303c4d;
}
.pager li > a, .pager li > span{
    background: #1c1c1c;
}
/*PODSTRONY*/
/*FOOTER*/
#footer .footer-center{
    background: #1c1c1c !important;
}
.footer .block .block-content:after{
    content: "Ciemny motyw by Maciejozaman";
}
/*FOOTER*/
}
