css:
@-moz-document domain("monkey-type.com") {
:root{
    --main-color: #FEFF03;
    --bg-color: #000002;
    --sub-color: #FFF3CD;
    --caret-color: var(--main-color);
    --correct: var(--main-color);
    --incorrect: var(--sub-color);
}
.word letter.incorrect {
    color: var(--incorrect);
}
.word.error{
    border-bottom: 2px solid var(--incorrect);
}
.word letter.incorrect.extra {
    color: var(--incorrect);
}
.word letter.correct {
    color: var(--correct);
}

