css:
@-moz-document url-prefix("https://alive.bar/web") {
    .ui {
        align-items: center;
    }

    .columns-area__panels {
        display: grid;
        grid: auto-flow / 1fr 1fr 600px 1fr 1fr;
    }

    .columns-area__panels__pane--compositional,
    .columns-area__panels__main {
        grid-column: 3;
    }

    .columns-area__panels__pane--navigational {
        grid-column: 2;
        grid-row: 1/3;
    }

    .navigation-panel {
        position: fixed;
    }

    .columns-area__panels__pane__inner {
        position: static;
        width: 100%;
    }

    .compose-panel {
        width: 100%;
    }

    .compose-panel .compose-form {
        min-height: auto;
    }

    .getting-started__footer {
        display: none;
    }
}
