@-moz-document domain("horsereality.com"), domain("horsereality.nl") {
.horse_banner::after {
    position: absolute; 
    content: ""; 
    height: 27px; 
    width: 110px; 
    background-image: url(https://www.horsereality.com/images/horse-reality-logo-small.png); 
    background-size: 110px 27px; 
    background-repeat: no-repeat; 
    background-position: bottom right; 
    top: 530px; 
    left: 720px; 
    z-index: 100000; 
    filter: brightness(1) drop-shadow(0 0 5px black); 
    opacity: 0.8; 
    
}


.caught_horserow::after {
    position: absolute; 
    content: ""; 
    height: 27px; 
    width: 110px; 
    background-image: url(https://www.horsereality.com/images/horse-reality-logo-small.png); 
    background-size: 110px 27px; 
    background-repeat: no-repeat; 
    background-position: bottom right; 
    top: 560px; 
    left: 610px; 
    z-index: 100000; 
    filter: brightness(1) drop-shadow(0 0 5px black); 
