css:
@-moz-document domain(tl.rulate.ru) {
#reader .text-wrapper {
	max-width: 1100px !important;
}
body:not(.brightness-1) #reader, body:not(.brightness-1) #readpage .content-text * * {
	background-color: #faf8f2 !important;
	color: #444 !important;
	font-family: Arial, serif !important;
	font-size: 20px !important;
	line-height: 24px !important;
}
body.brightness-1 #readpage .content-text * * {
	background-color: inherit !important;
}
}
