css:
@-moz-document url-prefix("http://samlib.ru/"), url-prefix("http://zhurnal.lib.ru/"), url-prefix("http://budclub.ru/") {
body {
    width: 1180px;
    margin: 0 auto;
    background-color: #e7ddc1;
}

dd font {
    font-size: 18px!important;
}
dd {
    font-size: 24px!important;
    margin: 0;
}
font {
    font-size: 18px!important;
}
li a b {
    font-size: 18px!important;
}
li small {
    font-size: 18px!important;
}
table {
    background-color: #e1d5b2;
}
td {
    background-color: #e7ddc1;
}
small {
    font-size: 16px!important;
}

a {
    color: #0f730f;
    font-weight: bold;
    text-decoration: none;
    transition: all 0.5s ease-in-out
}
a:hover {
    color: #0f1c73;
}
a:visited:hover {
    color: #0f1c73;
}
a:visited {
    color: #ff6464;
}
}
