css:
@-moz-document regexp("https?://(?:www\\.)?terraform\\.io/docs/.*") {
  body {
      background-color: #171812;  
      color: #F8F8F2;
  }

  h1, h2, h3, h4, h5 {
      color: #F8F8F2 !important;
  }

  a {
      color: #AE81FF !important;
  }

  #inner code {
      background-color: #272822;
      color: #F92672;
  }

  #inner a > code {
      color: #F92672;
  }

  #inner .alert-warning > p > code {
      background-color: #faebcc;
  }

  #inner pre.highlight {
      border-color: #75715E;
  }

  .highlight {
      background-color: #272822;
      border-color: #75715E;
  }

  .highlight .o {
      color: #F8F8F2;
  }

  #inner #inner-quicknav span > svg {
      fill: #666666;
  }

  #inner #inner-quicknav .active {
      background-color: #272822;
      border-color: #75715E;
  }
}
