css:
@-moz-document domain("en.wikipedia.org") {
#siteSub, .noprint, #mw-head, #mw-panel, .mw-editsection, #References, .sistersitebox, .reflist, #Bibliography, #External_links, .navbox, .catlinks, .mw-footer {
    display: none;
}
.mw-body {
    margin: 40px 8% 40px 8%;
    border: none;
    color: #1c78a6;
}
.mw-body h1, .mw-body-content h1 {
    border: none;
    margin: 40px 0 25px 0;
    color: #1c78a6;
}
.mw-body-content h2 {
    border: none;
    margin: 25px 0 15px 0;
    color: #1c78a6;
}
h3, .wikitable {
    border: none;
    margin: 15px 0 9px 0;
    color: #1c78a6;
}
}
