/* i really want this to be global */
@-moz-document regexp('.*fanserials\..+') {
    /* 
	The code in here applies to URLs having "fanserials." in it.
	For example http://fanserials.house
	*/
    body {
        background: center top no-repeat #000;
    }
    .wrapper main {
        margin: 0 auto 0!important;
    }
    .serial-box-promo, .promo-for-seirial-item-page, .right-col, .promo-vk, .bottom_serial_reklama {
        display:none!important
    }
    .left-col {
        width:100%!important
    }
}
