css:
@-moz-document url(chrome://browser/content/browser.xhtml) {
/*----------multiple bookmark----------*/
#PlacesToolbarItems .bookmark-item .bookmark-item menupopup {
  max-width:602px !important;
}

#PlacesToolbarItems .bookmark-item .bookmark-item{
  -moz-appearance: none !important;
  min-width:300px !important;
  max-width:300px !important;
  height:24px!important;  
}

#PlacesToolbarItems .bookmark-item .bookmark-item:nth-of-type(2n+1){
  margin-right:300px!important;
  margin-top:1px!important;
}

#PlacesToolbarItems .bookmark-item .bookmark-item:nth-of-type(2n){
  margin-left:300px!important;
  margin-top:-24px!important;

}
#PlacesToolbarItems .bookmark-item .bookmark-item:first-of-type{
  margin-top:0px!important;
}
.bookmark-item .openintabs-menuitem,.bookmark-item .bookmarks-actions-menuseparator{display:none!important;}
/*----------multiple bookmark----------*/ 
  
}
