css:
@-moz-document domain(learnpython.org) {
  body, nav.navbar, div.container-fluid {
    background: #212121 !important;
  }
  
  body, li, ul {
    color: #dbdbdb !important;
  }
  
  ul.breadcrumb {
    background: #323232;
  }
  
  /* navbar */
  nav.navbar-dark {
    background: #366994 !important;
  }
  
  .navbar a.nav-link {
    color: #515151 !important
  }
  	/* dropdowns */
  	div.dropdown-menu {
      background: #404040;
    }
  
  	a.dropdown-item:hover {
      background: #353535;
    }
  
  	a.dropdown-item {
      color: #dbdbdb;
    }
  	/* dropdowns */
  /* navbar */
  /* links */
  a {
    color: #366994;
  }
  
  a:visited, a:hover {
    color: #ffc331;
