css:
@-moz-document domain('aion.gameforge.com') {
  #vault-page li {
      height: 0px;
      border: 0px;
      margin: 0px;
      padding: 0px;
  }

  #vault-page .bonus_item_img {
      background: none;
      margin: 0px 4px 0px 0px;
  }

  #vault-page .bonus_item_img div {
      margin: 0px;
  }

  #vault-page .item-info {
      height: 40px;
  }

  #vault-page .item-info .info-text {
      overflow: hidden; 
      height: 30px;
  }

  #vault-page .item-info .info-text :not(:first-child) {
      display: none;
  }

  #vault-page .item.standard {
      height: 40px;
  }

  #vault-page .item.standard > label {
      display: none;
  }

  #vault-page .vault-category .allocate_all {
      margin: 7px 25px;
  }
}
