css:
@-moz-document domain("cndf147.blogspot.com") {
.FeaturedPost,
.PopularPosts,
.blog-pager,
.blog-feeds,
.cap-top,
.cap-bottom,
.tabs-outer,
.post-header,
.post-footer,
.post-title,
.navbar,
.foot,
.fauxcolumn-outer,
.fauxborder-right,
.column-right-outer,
.column-left-outer,
.body-fauxcolumns,
.content-fauxcolumns,
.comments,
footer,
header {
    display: none;
}
body  {
    background-color: black;
    background-image: none;
}
body,
.main-inner,
.main-inner .columns,
.main-inner .column-center-inner,
.main-inner .column-center-inner .section,
.content-inner,
.post, .widget{
    padding: 0;
    text-align: center;
}
body,
html body .region-inner,
.content-outer, .content-fauxcolumn-outer, .region-inner,
.column-center-outer {
    min-width: auto !important;
    max-width: none !important;
    width: auto !important;
}
body,
.post,
.columns-inner,
.column-center-outer {
    width: 100% !important;
    height: 100% !important;
    margin: auto auto;
}
}
