css:
@-moz-document domain(abv.bg){
	html, body {
    	color: grey !important;
    	background: #202025 !important;
        font: 18px Ubuntu, Verdana, sans-serif;
	}
	.mainMenu .gwt-MenuItem {
	    color: #d7d2d2;
	}
	.foldersRow {
	    color: #dcdbdb;
	}
	.abv-mr em {
	    color: #759ee2;
	}
	.abv-mr span {
	    color: #bb5eef;
	}
	.abv-grayBG {
	    background: #404040;
	}
	#inboxTable {
	    background: #303030;
	    color: #6ac6ed;
	}
  
   /*1.0.1*/
	.unread {
    	color: #fc7f7f !important;
	}
	.settingsHoveredRow, .settingsRow:hover, .settingsSelectedRow, .foldersRow:hover, .foldersSelectedRow, .foldersHoveredRow {
    	background: #404040;
	}
	.abv-emptyFolder {
   		 background: #303030;
	}
	#searchFieldInbox {
    	border: 2px solid #1E930C;
	}
	.abv-searchField, #searchFieldInbox {
    	background: #303030;
    	color: #1E930C;
	}
	#header {
    	background: #252525;
    	border-bottom: 1px solid #8e2525;
    	font: 15px Ubuntu, Verdana, sans-serif;
	}
	.abv-mailText {
    	color: #858585 !important;
    }
	.abv-headerRight .abv-settingsTriger:first-child {
    	padding-right: 60px;
    }
  
   /*1.0.3*/
	.gwt-PopupPanel.foldersContextMenu, .foldersContextMenu {
    	background-color: #272622;
    }
	.gwt-MenuItem {
    	color: #ea8c21;
	}
	.dialogBoxExt>div {
    	border: 3px solid #dcbf79;
    	background: #151515;
	}
	.chat-Caption h2 {
    	color: #8c8b8b;
	}
	.gwt-PopupPanelGlass {
    	background: rgba(0, 0, 0, 0.80) !important;
    }
     /*1.0.7*/
    .menuPopupContent > div, .settingsMenuDropdown {
    	border: 3px solid #dcbf79;
    	background: #303030;
    	color: #8c8b8b;
    }
}
