css:
@-moz-document url-prefix("https://quickref.dev/") {
html,
body,
header,
footer {
  background-color: #282c35;
  color: white;
}

header > form {
  background-color: #F2EFE4;
}

h1 {
  color: rgba(255, 255, 255, 0.88);
}

code {
  background-color: darkgray;
  color: rgba(255, 255, 255, 0.88);
}

a {
  color: #ffd166;
}

p.snippet {
  color: lightgray !important;
}

.results_container .results .result .url {
  color: burlywood !important;
}
}
