css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("whatfinger.com"), domain("www.whatfinger.com") {
  .vc_col-sm-6, .bottom-headlines-wrapper, .wpb_button, .wpb_content_element, ul.wpb_thumbnails-fluid > li, .smile-icon-left-side-headlines-wrap .left-side-headlines-feature-item.headline-item, .smile-icon-religion-culture-links-wrap, .aio-icon-component, .wpb_column > .wpb_wrapper .aio-icon-component, .vc_empty_space, .smile-icon-left-side-headlines-wrap, .welcome-heading, .vc_row[data-vc-full-width], .smile-icon-headline-links2-wrap, .headline-links-feature-item .headline-links-header-block .headline-links-header img, #sq-colophon {
      display: none !important;
  }

  .creative-link, div.flash-news {
      width: 100% !important;
  }
  .smile-icon-headline-links-wrap .headline-links-feature-item.headline-item {
      text-align: left !important;
      transition-property: background-color !important;
      transition-duration: .35s, .25s !important;
      transition-timing-function: ease-out !important;
  }

  .smile-icon-headline-links-wrap .headline-links-feature-item.headline-item:hover {
      background: #d1f5d5;

  }

  body {
      background: #fcfcfc;
  }
}
