css:
@-moz-document domain("englishlightnovels.com") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

body, html
{
  background: #3d3d3f;
}

#respond .comment-form-service, #respond #comment-form-identity #comment-form-nascar
{
    background: #363639;
}

input, textarea,

#respond #comment-form-comment, #comment-form-share-text-padder,
#respond .comment-form-fields div.comment-form-input,

.main-navigation,

div#actionbar,
div#actionbar .actnbr-popover .tip-inner
{
    background: #2e2e2f !important;
}

button
{
  background-color: #5387ff !important;
}

a, .entry-footer
{
  color: #5387ff !important;
}

body, h1, h2, h3, h4, h5, h6, ::placeholder,
input, textarea, p span,

#masthead a,

#respond div.comment-form-field
{
  color: #ccc !important;
}

#jp-relatedposts .jp-relatedposts-items .jp-relatedposts-post .jp-relatedposts-post-title a
{
    font-weight: inherit;
}

*
{
  border: none !important;
  box-shadow: none !important;
}

div#actionbar .actnbr-popover.tip-top-left .tip-arrow::before
{
  border-top-color: #2e2e2f;
}

h3 
{
    border-top: 1px solid rgba(255, 255, 255, .42) !important;
}

.page-content ul, .entry-content ul, .entry-title ul
{
    list-style: none;
}

.featured-header-image
{
  margin: 0;
  margin-bottom: -400px;
  
  pointer-events: none;
}

.entry-title, .entry-title a, .entry-title a:visited,
.wp-block-image img
{
  z-index: 10000;
  position: relative;
    filter: drop-shadow(0px 0px 5px #3d3d3f) drop-shadow(0px 0px 3px #3d3d3f);
}

.sidebar-left,
.sidebar-right
{
  z-index: 10000;
  position: absolute;
  
  background: #3d3d3f75;
  padding: 10px;
}

.site-branding:before
{
  z-index: 0;
}

.site-title, .site-title a
{
  z-index: 5;
  position: relative;
}

.hentry
{
  z-index: 5;
  position: relative;
}

.site-main
{
  width: 52.5%;
  margin-left: 10%;

}

input[type="search"].search-field,
#subscribe-email input
{
  width: 100% !important;
}

.featured-header-image img,
.featured-header-image > img
{
  height: 400px;

  object-fit: cover;
}

.site-content
{
  padding: 0;
}

.sidebar-left
{
  margin-left: 3%;
  right: 8%;
}

.widget.widget_text img
{
  margin: 15px auto !important;
  width: 200px;
  height: auto;
  object-fit: cover;
  display: block;
}

.sidebar-right
{
  margin-left: 0;
  margin-right: 0%;
  padding-right: 3%;
}

.sidebar-right
{
  margin-top: 0;
  right: -14%;
  opacity: .1;
  
  border-left: 3px solid #ccc !important;
  
  transition: right .2s, opacity .2s;
}

.sidebar-right:hover
{
  margin-top: 0;
  right: 0px;
  opacity: 1;
  
  background-color: #363639;
  border-left: 3px solid #ccc0 !important;
  box-shadow: 0px 0px 15px #111 !important;
  
  transition: right .2s, opacity .2s;
}


body, html
{
  overflow-x: hidden;
}

.featured-header-image img
{
  filter: blur(2px);
  -webkit-mask-image: -webkit-gradient(linear, left top, left bottom, 
    from(rgb(0, 0, 0)), to(rgba(0,0,0,0)));
  opacity: .3;
}
}
