css:
@-moz-document domain("pcdvd.com.tw") {

*{
    font-family: "KaiGen Gothic TW", "Noto Sans CJK TC", "Microsoft YaHei", "Microsoft JhengHei", Helvetica;
}

.smallfont {
    font: 10pt  "KaiGen Gothic TW", "Noto Sans CJK TC", "Microsoft YaHei", "Microsoft JhengHei", Helvetica;
}

td, th, p, li {
    font: 12pt "KaiGen Gothic TW", "Noto Sans CJK TC", "Microsoft YaHei", "Microsoft JhengHei", Helvetica;
}

@media (min-width: 1200px){
    html{
        zoom: 125%;
    }
  
  td img{
        max-width: calc(100vw - 400pt);
    }
}
  
  @media (min-width: 1400px){
  td img{
        max-width: calc(100vw - 425pt);
    }
}

@media (min-width: 1600px){
    html{
        zoom: 150%;
    }
  
  td img{
        max-width: calc(100vw - 700pt);
    }
}
}
