@-moz-document domain("moons-tavern.com") {
body, html {
    font-family: "/*[[mtfont]]*/", sans-serif !important;
    color: #d4d4d4;
    scrollbar-color: #444444 #222222;
    background-color: #242424 !important;
}
h1, h2, h3, h4, a, cite, em, strong {
    color: #eeeeee !important;
}
a {
    text-decoration: underline !important;
}
p {
    font-size: /*[[mtsize]]*/;
    line-height: 1.4em;
    text-align: justify;
    font-weight: normal;
}
div.main-inner {
    padding: 0 !important;
    margin: 0 !important;
    background-color: #181818 !important;
}
div.main-inner section#content.content {
    margin: 0 4em 4em 4em;
    float: none;
    width: inherit;
    background-color: #242424 !important;
}
div.main-inner section#content.content div.page-title,
footer#footer section#footer-bottom a#back-to-top,
div.wpautoterms-footer {
    background-color: #181818 !important;
}
div.sidebar.s1,
div.sidebar.s2,
section#comments,
footer#footer section#footer-widgets {
    display: none;
}
@media screen and (max-width:900px) {
    div.main-inner section#content.content {
        margin: 0 !important;
        padding: 0;
    }
    p {
        font-size: /*[[mtmobsize]]*/;
    }
}
}
