css:
@-moz-document url(https://my.playstation.com/whatsnew)
{
body
{
    padding: 0px !important;
    overflow: hidden !important;
}

#sony-bar
{
    display: none !important;
}

#sb-messages-dropdown
{
    display: block !important;
    position: fixed !important;
    width: 100% !important;
    height: 100vh !important;
    left: 0 !important;
    top: 0 !important;
    z-index: 10000 !important;
    margin: 0px !important;
}

.sb-messages-panel
{
    background-color: rgba(30, 32, 33, 1) !important;
    height: 100vh !important;
    width: 100% !important;
}

.sb-messages-panel__left-pane
{
    background-color: rgba(30, 32, 33, 1) !important;
    display: inline-block !important;
}

.sb-messages-panel__right-pane
{
    background-color: rgba(30, 32, 33, 1) !important;
    display: inline-block !important;
    height: 100vh !important;
    width: 100% !important;
}

.sb-messages-panel__list-container,
.sb-messages-panel__right-pane,
.sb-chat-window
{
    height: 100vh !important;
    width: 100% !important;
}

.sb-chat-messages-container
{
    height: calc(100% - 89px) !important;
}

.sb-drop-attachment,
.sb-chat-messages
{
    height: 100%;
}

.sb-chat-messages__scroller-container
{
    height: 100% !important;
}
}
