css:
@-moz-document domain(yugioh.fandom.com) {

.WikiaPage .WikiaPageBackground {
    background: #292929;
}

body {
    color: rgba(255,255,255, 0.7);
}

.WikiaPage .page-header__title {
    color: rgba(255,193,7, 0.8)
}

header.wds-community-header {
    background-color: #292929;
}

.wds-community-header__top-container, .wds-community-header__local-navigation {
    background-color: #353535;
}

.wds-community-header .wds-community-header__sitename a:hover {
    color: rgba(255,193,7, 0.8);
}

.wds-community-header .wds-community-header__sitename a {
    color: #a40000;
}

.portable-infobox.pi-background {
    background-color: #353535;
    color: rgba(255,255,255, 0.7);
}

a {
    color: rgba(255,193,7, 0.8);
}

a#ShareEntryPoint, a.entry-button.wds-is-secondary.wds-button {
    color:rgba(255,255,255, 0.7);
}

.WikiaRail .activity-module .edit-info .edit-info-user, .WikiaRail .activity-module .edit-info .edit-info-time {
    color: rgba(255,255,255, 0.7);
}

.WikiaRail .premium-recirculation-rail a {
    color: rgba(255,255,255, 0.7);
}

.wds-community-header__wordmark {
    margin-bottom: 0px;
    margin-top: 0px;
    margin-left: 0px;
    padding-bottom: 40px;
    background-color: #353535;
}

.toccolours {
    background-color: #353535;
}

td {
    background-color: #292929;
    text-align: center;
    border-radius: 0.4rem;
}
  
}
