css:
@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/") {
/*此部分为用户自设定部分*/
#eeee77 {
    background: /*[[maincolor]]*/;
}
table tbody th {
    background:/*[[maincolor]]*/;
}
center > table > tbody > tr > td > p > font {
    color:/*[[maincolor]]*/;
}
body>center:nth-child(1){
    background:white;
}
#eeee77 a:hover {
    background-color:/*[[lightcolor]]*/;
}
/*以上是设定部分，下面请勿乱动*/
/*代码2部分还有可修改部分*/
* {
    margin: 0;
    padding: 0;
}
#eeee77 {
    width: 70px;
    box-shadow: 0px 8px 15px #a2a2a2;
}
#eeee77 a {
    font-family: none;
    font-size: 20px;
    display: inline-block;
    width: 70px;
    text-align: center;
    height: 50px;
    line-height: 50px;
}

#eeee77 br {
    line-height: 2px;
}
#eeee77 br:nth-child(1) {
    line-height: 70px;
}
body {
    background: url("http://www.pptbz.com/d/file/p/201708/a1d07b6201af8f574b6539cb724bbc16.png");
    background-repeat: no-repeat;
    background-size: 100% 100%;
    background-attachment: fixed;
}

body > center:nth-child(1) {
    box-shadow: 0px 8px 15px #a2a2a2;
    border: none;
}
body > hr {
    display: none;
}
center > table > tbody > tr > td > p > font {
    font-family: none;
    font-size: 40px;
    line-height: 90px;
    font-weight: bold;
}

body > center {
    margin-bottom: 50px;
}
table center p > font {
    font-size: 15px;
}

table tbody th {
    color: #fff;
}
form {
    margin-bottom: 30px;
}
body > table center > p font:after{
    content:"感谢使用由Haraki开发的H2WOJ.";
    line-height:40px;
    color:#555;
}
}

@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/status.php"), url-prefix("http://www.ssoier.cn:7077/wst/statusx.php") {
/*Luogu调色款*/
.ac font,.ac{
    color: /*[[clac]]*/;
}
.ce font {
    color: /*[[clce]]*/;
}
.re {
    color: /*[[clre]]*/;
}
.wa,.wa font{
    color: /*[[clwa]]*/;
}
.tle,
.mle,
.ole {
   color: /*[[cltmo]]*/;
}
.wa font:before {
    content: "WA ";
}
.ac font:before {
    content: "AC ";
}

.ce font:before {
    content: "CE ";
}
center>center table tr td:nth-child(2){
    text-align:center;
}
.runid_link:hover{
    color: /*[[maincolor]]*/;
}
center>center table{
    background:white;
    border-collapse:collapse;
    box-shadow: 0px 8px 15px #a2a2a2;
    margin-bottom:40px;
    padding-bottom:30px;
}
center>center table:hover{
    box-shadow: 0px 10px 20px #999;
}
center>center table tr{
    height:35px;
}
center>center table tr:nth-child(1){
    height:40px;
}

center>center table tr td a:hover{
    color:/*[[maincolor]]*/;
}

.status tr td,.status td select,.status input{
    font-size:17px;
}
.status input{
    margin-right:10px;
}
}

@-moz-document url("http://www.ssoier.cn:7077/wst/"), url("http://www.ssoier.cn:7077/wst/index.php") {
body > table p font {
    color: black;
    font-weight: bold;
}
body > table p font:before {
    content: "「 ";
}
body > table p font:after {
    content: " 」";
}
body > table center > p font {
    color: black;
    font-weight: normal;
}
body > table center > p font:before {
    content: "";
}
body > table center > p font:after {
    content:"感谢使用由Haraki开发的H2WOJ.";
    line-height:40px;
    color:#555;
}
/*login*/
form table {
    margin: 0 auto;
}
form table table tbody tr td {
    font-size: 25px;
    font-family: none;
}
form table tr td input{
    width:17em;
}
form>table{
    width:400px;
}
form table tr:nth-child(3) input{
    border:0;
    font-size:15px;
    width:4em;
     padding:7px;
    border-radius:7px;
}
form table tr:nth-child(3) a{
    color:white;
    background: /*[[maincolor]]*/;
    font-size:15px;
    padding:10px;
    width:4em;
    border-radius:7px;
}
form table tr:nth-child(3) input:hover{
        box-shadow: 0px 8px 15px #a2a2a2;
}
form table tr:nth-child(3) a:hover{
        box-shadow: 0px 8px 15px #a2a2a2;
}
}

@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/problem_show.php?") {
.pcontent h3 {
    font-size: 20px;
    line-height: 50px;
}
.xxbox p {
    line-height: 30px;
}
body > center:nth-last-child(7),
body > center:nth-last-child(6) {
    box-shadow: 0px 8px 15px #a2a2a2;
    border: none;
    background:white;
}
/* P.S:这个6,7的由来非常之神奇 我一个一个试的*/
}

@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/ranklist.php") {
body > center {
    margin-bottom: 30px;
}
caption {
    margin-bottom: 20px;
}
caption a:before {
    content: "User ";
}

body>center:nth-last-child(3) table{
    background:white;
    border-collapse:collapse;
    box-shadow: 0px 8px 15px #a2a2a2;
    margin-bottom:40px;
    padding-bottom:30px;
}
body>center:nth-last-child(3)  table:hover{
    box-shadow: 0px 10px 20px #999;
}
body>center:nth-last-child(3)  table tr{
    height:35px;
}
body>center:nth-last-child(3)  table tr:nth-child(1){
    height:40px;
}

body>center:nth-last-child(3)  table tr td a:hover{
    color:/*[[maincolor]]*/;
}

}

@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/problem_list.php") {
body>center:nth-last-child(3) table:nth-child(2){
    background:white;
    border-collapse:collapse;
    box-shadow: 0px 8px 15px #a2a2a2;
    margin-bottom:40px;
    padding-bottom:30px;
    width:1000px;
}
body>center:nth-last-child(3) table:nth-child(2):hover{
    box-shadow: 0px 10px 20px #999;
}
body>center:nth-last-child(3) table tr{
    height:35px;
}
body>center:nth-last-child(3) table tr:nth-child(1){
    height:40px;
}

body>center:nth-last-child(3) table tr td a:hover{
    color:/*[[maincolor]]*/;
}

.status tr td,.status td select,.status input{
    font-size:17px;
}
.status input{
    margin-right:10px;
}
}

@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/mail_index.php"), url-prefix("http://www.ssoier.cn:7077/wst/mail_send.php"), url-prefix("http://www.ssoier.cn:7077/wst/mail_send_result.php") {

A.bottom_link:visited{
    color:white;
}
A.bottom_link:link{
    color:white;
}
A.bottom_link{
    color:white;
}
A.bottom_link:hover{
    color:white;
    text-decoration:none;
    box-shadow: 0px 8px 15px #a2a2a2;
}
.bottom_link{
    background:/*[[maincolor]]*/;
    padding:10px;
    display:inline-block;
    border-radius:7px;
    margin-top:40px;
    color:white;
}
caption.mail{
    color:/*[[maincolor]]*/;
}
textarea{
    resize: none;
    width:82%;
    padding:0 1%;
}
form table tbody tr:nth-child(5) td input{
    height:35px;
    width:60px;
    font-size:15px;
    border:0;
    margin-top:20px;
    margin-right:30px;
    background:#ccc;
    border-radius:5px;
}
form table tbody tr:nth-child(5) td input:nth-child(1){
    background:/*[[maincolor]]*/;
}
form table tbody tr:nth-child(5) td input:hover{
   box-shadow: 0px 8px 15px #a2a2a2;
}
.warn{
    color:/*[[maincolor]]*/;
    font-weight:bold;
}
}

@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/mail_index.php") {
body>center:nth-last-child(3) {
    font-size:25px;
}
body>center:nth-last-child(3) tbody tr td:nth-child(2){
    text-align:center;

}
body>center:nth-last-child(3) tbody tr td,body>center:nth-last-child(3) tbody tr th{
    border:0;
    height:35px;
}
body>center:nth-last-child(3) tbody{
    background:white;
    box-shadow: 0px 8px 15px #a2a2a2;

}
body>center:nth-last-child(3) tbody:hover{
    box-shadow: 0px 10px 20px #999;
}
body>center:nth-last-child(3) table{
    margin-top:30px;
        border-collapse:collapse;
    border:0px;
}
}

@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/mail_send_result.php") {
A.bottom_link{
    margin-left:20px;
}
}

@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/mail_show.php") {
body > center:nth-last-child(6) {
    box-shadow: 0px 8px 15px #a2a2a2;
    border: none;
}
}

@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/userinfo.php") {
center>h3{
    color:/*[[maincolor]]*/;
    padding-top:30px;
}
}

@-moz-document url-prefix("http://www.ssoier.cn:7077/wst/show_source.php") {
center th h3{
    color:white;
    font-size:35px;
}
center th br{
    line-height:10px;
}
body>center:nth-last-child(3) th{
    padding:10px 0;
}
body center pre{
    background:#eee;
    width:90%;
    padding:2% 5%;
    padding-bottom:5%;
    font-size:19px;
    font-family:consolas;
}
body>center:nth-last-child(3) tbody{
    box-shadow: 0px 8px 15px #a2a2a2;
}
body>center:nth-last-child(3) tbody:hover{
    box-shadow: 0px 10px 20px #999;
}
pre.sh_sourceCode .sh_string{
    font-family:consolas;
}

pre.sh_sourceCode .sh_preproc{
    color:#008000;
    font-weight:normal;
}
pre.sh_sourceCode .sh_string{
    color:#0000ff;
    font-weight:normal;
}
pre.sh_sourceCode .sh_keyword{
    color:#000000;
    font-weight:bold;
}
pre.sh_sourceCode .sh_symbol{
    color:#ff0000;
    font-weight:normal;
}
pre.sh_sourceCode .sh_type{
    color:#000000;
    font-weight:bold;
}
pre.sh_sourceCode .sh_number{
    color:#800080;
    font-weight:normal;
}
pre.sh_sourceCode .sh_function{
    color:#000;
    font-weight:normal;
}



}
