/* i really want this to be global */

@keyframes rainbow {
    0% {background-color: pink;border-color: pink;}
    14% {background-color: peachpuff;border-color: peachpuff;}
    28% {background-color: oldlace;border-color: oldlace;}
    42% {background-color: honeydew;border-color: honeydew;}
    56% {background-color: lightcyan;border-color: lightcyan;}
    80% {background-color: lavender;border-color: lavender;}
    94% {background-color: lavenderblush;border-color: lavenderblush;}
    100% {background-color: pink;border-color: pink;}
}

a.btn-common-play-game-lg {
    animation: rainbow /*[[speed]]*/s infinite;
}
