css:
@-moz-document domain("tapas.io") {
.toolbar, .top-nav {
    opacity: 0;
    /*height: 20px;*/
    transition-property: opacity; 
    transition-duration: 0.2s;
    transition-timing-function: easeOutExpo;
}

.toolbar:hover, .top-nav:hover {
    opacity:0.8;
}

.toolbar__row--center {
    display: none !important;
}
.ico {
      transform: scale(0.7);
}
}
