/* 
For adding a custom background easily to MAL the forum and profile pages.
Install with the Stylish add-on. Compatible with other MAL themes!
By Shishio-kun @MyAnimeList
If you need help or want more themes see this page:
http://myanimelist.net/forum/?topicid=453219
*/ 

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp(".*myanimelist\\.net/(?!animelist|mangalist|editlist).*") 
{ 
  
.profile .user-statistics .stats-graph .graph.watching,
.profile .user-statistics .stats-graph .graph.reading,
.profile .statistics-updates .data .graph .graph-inner.anime.watching,
.profile .statistics-updates .data .graph .graph-inner.manga.reading {
    background-color: #2db039 !important;
}


.profile .user-statistics .stats-graph .graph.completed,
.profile .statistics-updates .data .graph .graph-inner.anime.completed,
.profile .statistics-updates .data .graph .graph-inner.manga.completed {
    background-color: #26448f !important;
}

.profile .user-statistics .stats-graph .graph.on_hold,
.profile .statistics-updates .data .graph .graph-inner.anime.on_hold,
.profile .statistics-updates .data .graph .graph-inner.manga.on_hold{
    background-color: #f9d457 !important;
}

.profile .user-statistics .stats-graph .graph.dropped,
.profile .statistics-updates .data .graph .graph-inner.anime.dropped,
.profile .statistics-updates .data .graph .graph-inner.manga.dropped
{
    background-color: #a12f31 !important;
}

.profile .user-statistics .stats-graph .graph.plan_to_watch,
.profile .user-statistics .stats-graph .graph.plan_to_read,
.profile .statistics-updates .data .graph .graph-inner.plan_to_watch,
.profile .statistics-updates .data .graph .graph-inner.manga.plan_to_read{
    background-color: #c3c3c3 !important;
}


.profile .user-statistics .stats-status .circle.watching:after, .profile .user-statistics .stats-status .circle.reading:after {
    background-color: #2db039 !important;
}

.profile .user-statistics .stats-status .circle.completed:after {
    background-color: #26448f !important;
}

.profile .user-statistics .stats-status .circle.on_hold:after {
    background-color: #f9d457 !important;
}

.profile .user-statistics .stats-status .circle.dropped:after {
    background-color: #a12f31 !important;
}

.profile .user-statistics .stats-status .circle.plan_to_watch:after, .profile .user-statistics .stats-status .circle.plan_to_read:after {
    background-color: #c3c3c3 !important;
}

}
