css:
@-moz-document url-prefix("https://dev.twitch.tv/docs"), url-prefix("https://dev.twitch.tv/products"), url-prefix("https://dev.twitch.tv/showcase"), url-prefix("https://dev.twitch.tv/support") {
body, header .sidebar dl dt a, header .sidebar dl dd a, .btn.secondary {
    color: rgb(214, 216, 218);
    background-color: rgb(36, 41, 46);
}
a, header .sidebar dl dd a.active-highlight, .story .story__content h4 a {
    color: rgb(165 197 255);
}
header .sidebar dl dt.search input {
    background: rgba(255, 255, 255, 0.10);
}
.main, .content, header .sidebar dl dt a:hover, .doc-content {
    background: rgb(49 54 63);
}
header .sidebar, .hero {
    background: rgb(27, 29, 35);
}
table tr:nth-child(odd), table thead + tbody tr:nth-child(even) {
    background: rgb(36, 41, 46);
}
code {
    background: rgb(36, 41, 46) !important;
    color: rgb(236 137 137) !important;
}
pre {
    background-color: rgb(36, 41, 46) !important;
    overflow-x: auto;
}
blockquote {
    background: rgb(74 49 123);
}
.btn, .bright-cta {
    background: rgb(145 70 255 / 0.25);
}
}
