css:
@-moz-document domain("codeclimate.com") {
body, .content {
    background-color: #222;
    color: #ccc;
}

.btn.btn--primary:disabled, .btn.btn--primary.btn--disabled {
    background-color: #5ca47a;
    border-color: #5ca47a;
    color: #d2d2d2
}

pre>code[class*="language-"] {
    border-color: #444;
    background-color: #262626;
}

pre>code[class*="language-"]>div::before {
    border-color: #444;
}

code {
    background-color: #333;
    color: #fff;
}

code .token.operator, code .token.entity, code .token.url, code .language-css .token.string, code .style .token.string {
    background: transparent;
    color: #fff;
}

.table thead td {
    background-color: #333;
    border-color: #444;
}

.table tbody td {
    border-color: #444;
}

.repo, .repo .measure, .repo:hover .repo__metric {
    color: #999;
}
}
