css:
@-moz-document domain("nzz.ch")
{
	@media print {
		#kommentare, .container--comments, .sharebox, .headline__meta-extra, .actionbar, .actionbar--header, .pageelement--moretosubject {
			display: none !important;
		}
		body {
			display: block !important;
			width: 100% !important;
			padding-left: 4em !important;
			padding-right: 4em !important;
		}
		.header, .pageholder, .container, .container--article, .headline__inner, .headline__lead, .headline__meta, .articlecomponent, .image, .image--content, .text {
			width: 100% !important;
			min-width: 100% !important;
			padding-left: 0 !important;
			padding-right: 0 !important;
		}
	}
}
