css:
@-moz-document domain("tieba.baidu.com") {
.voice_player .speaker:after{
    line-height: 1em;
    font-size:10em;
    color:#c3c9;
    text-align: center;
    content:'疑似麦片';
    position: absolute;
    pointer-events: none;
    box-shadow: 0 0 250px  50px#f999;
    width: 50vw;left: -1em;
    height: 25vh;top: 0;
    background: #f999;
    
}
.voice_player .time:after{
    line-height: 1em;
    font-size:2.5em;
    color:#c3c;
    text-align: center;
    content:'本页疑似有麦片';
    position: fixed;
    pointer-events: none;
    width: 100vw;left: 0;
    height: 2em;top: 1vh;
    background: #f999;
    z-index:99999;
}
}
