/* ==UserStyle==
@name           unshort.link - blue / green
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Blue / green for unshort.link
@author         Thorsten Willert
==/UserStyle== */
@-moz-document domain("unshort.link") {

    * {
        font-family: Tahoma, Arial, Helvetica !important;
    }

    body {
        background: #1A2980;
        background: -webkit-linear-gradient(to bottom, #26D0CE, #1A2980);
        background: linear-gradient(to bottom, #26D0CE, #1A2980);
    }

    #buttonRedirect {
        filter: invert();
        font-weight: bold;
    }

    .logo,
    #buttonRedirect,
    #unshortDirectButton,
    #headerLinks,
    a:hover {
        filter: invert();
    }
    
    #urlDirect,
    .btn-primary:focus, .btn-primary:active,
    #redirectLinkTextArea{
        color: black;
        background-color: rgba(255,255,255,0.5) !important;
        border: 1px solid rgba(255,255,255,0.9) !important;
    }
}
