/* ==UserStyle==
@name           unshort.link - dark red
@namespace      github.com/openstyles/stylus
@version        1.5.0
@description    Dark red for unshort.link
@author         Thorsten Willert
==/UserStyle== */
@-moz-document domain("unshort.link") {

    * {
        font-family: Tahoma, Arial, Helvetica !important;
    }

    body {
        background: #8E0E00;
        background: -webkit-linear-gradient(to bottom, #1F1C18, #8E0E00);
        background: linear-gradient(to bottom, #1F1C18, #8E0E00);
    }

    #buttonRedirect {
        filter: invert();
        font-weight: bold;
    }


    .logo,
    #buttonRedirect,
    #unshortDirectButton,
    #headerLinks,
    a:hover {
        filter: grayscale();
        mix-blend-mode: screen;
    }
    
     .btn-primary:focus, .btn-primary:active,
    #redirectLinkTextArea{
        background-color: rgba(255,255,255,0.5) !important;
        border: 1px solid rgba(255,255,255,0.9) !important;
    }
}
