css:
@-moz-document url-prefix("https://books20.sci2u.dk/books/") {
#page-content-wrapper > .container-fluid {
	max-width: calc(720px + 12em);
	border: 1px solid #ccc;
}
#page-content-wrapper > .container-fluid > div {
	margin: 3em 6em 
}

#wrapper {
	display: grid;
	grid-template-columns: 0 1fr;
	padding: 0 !important;
	width: 100%;
	border: 0;
}
#wrapper.toggled {
	grid-template-columns: 270px 1fr;
}

#sidebar-wrapper {
	grid-column:1/2;
	margin-top: 2em;
}

#page-content-wrapper {
	position: static !important;
	grid-column: 2/3;
}
}
