css:
@-moz-document domain("zportal.nl") {
.zermeloSideMenu {
    background-color: #0000ff;
}
.zermeloTitleSmall {
    color: #000000;
}
.zermeloSideMenu,.zermeloSideMenuItem {
    background-color: /*[[color]]*/;
}
.zermeloSideMenuItem:hover {
    background-color: /*[[color]]*/;
}
.mainMenuIcon {
    background-color: /*[[color]]*/;
}
div[eventproxy="isc_Img_1"], div[eventproxy="isc_Img_3"], div[eventproxy="isc_Img_5"] {
    filter: brightness(0);
}
.zermeloTopBar {
    filter: saturate(0);
}
a, *, h2 {
    color: /*[[color]]*/;
}
.zermeloModule * {
    color: #000000
}
.form .logo, .form .button {
    filter: hue-rotate(250deg);
}
}
