css:
@-moz-document domain("wpedia.goo.ne.jp") {
#hotword, #NR-footer-sv, .NR-pr, .gooad-long, .sphead > header:nth-child(2) > nav:nth-child(3), #incontents > div:nth-child(1), .NR-ad {
    display: none;
}

#incontents p ,#bodyContent > p a, #bodyContent > ul a, #bodyContent > #contentSub > ul a, #mw-content-text p a {
    font-size: 16px;
    line-height: 1.7;
}

#bodyContent > p, #bodyContent > #contentSub p, #mw-content-text p {
    padding: 16px 10px 0;
}

a, abbr, address, article, aside, audio, b, blockquote, body, canvas, caption, cite, code, dd, del, details, dfn, div, dl, dt, em, fieldset, figcaption, figure, footer, form, h1, h2, h3, h4, h5, h6, header, hgroup, html, i, iframe, img, ins, kbd, label, legend, li, mark, menu, nav, object, ol, p, pre, q, samp, section, small, span, strong, sub, summary, sup, table, tbody, td, tfoot, th, thead, time, tr, ul, var, video {
	font-size: 101%;
}
}

@-moz-document url-prefix("https://wpedia.goo.ne.jp/wiki/") {
#rside {
    display: none;
}

#main {
    width: 95%;
    max-width: 1200px;
    float: none;
    margin: 0 auto;
}

#middle, #inmain {
     width: 100%;
}

#content {
    padding: 16px 32px;
}
}
