/*	@name				Youtube Red Scrollbar 
*	@version			v1.0
*	@description		Makes your scrollbar red. That's it! 
*	@author				Techusiast - RolandUdv
*   @website            http://techusiast.com/
*   @stylish            
*	@github 			https://github.com/RolandUdv/Youtube-Red-Scrollbar
*	@license			CC-BY
*/

@-moz-document domain("youtube.com") {

    ytd-thumbnail #thumbnail.ytd-thumbnail,
    img.yt-img-shadow {
        border-radius: 10px;
        background: #181818;
    }

    /* Notification thumbnail images */

    ::-webkit-scrollbar {
        width: 10px;
    }

    ::-webkit-scrollbar-track {
        background: #181818;
    }

    ::-webkit-scrollbar-thumb {
        background: #ff0000;
    }

    ::-webkit-scrollbar-thumb:hover {
        background: #ff5b5b;
    }
}
