css:
@-moz-document url-prefix("https://mymoodle.lnu.se") {

html, body, #region-main, .block>.content, #dockeditempanel .dockeditempanel_bd, #intro.generalbox, .dropdown-menu {
    background-color: #333 !important;
    color: #fff !important;
}

h1, h2, h3, h4, h5, h6, .block .header h2, .table, table.collection, table.flexible, .generaltable {
    color: #fff !important;
}

.breadcrumb, .block .header, .course-info-container, .coursename {
    background: none!important;
    background-color: #111!important;
    color: #fff;
}



a, span, .block .content a, .block .content li a, .block .content td a, .dropdown-item {
    color: #FFE500;
}

.activity_footer .text {
    color: #fff;
}

.inmplayer-panel-header, .inmplayer-trigger {
    background-color: #FFE500;
    color: #000;
}

}
