@-moz-document url-prefix("https://www.twitch.tv/") {
    /* ==UserStyle==
     @name				    Twitch Future Funk
     @namespace		    duogals
     @version		         1.0.1
     @description	        A future funk themed style for Twitch.tv.
     @author			 	  duogals/Shadder
     @license		    	  CC0 - Public Domain
     @homepageURL	 https://github.com/duogals/Twitch-Future-Funk/
     @supportURL		 https://github.com/duogals/Twitch-Future-Funk/issues
     @updateURL		     https://raw.githubusercontent.com/cicerakes/DarkNight-MyAnimeList/master/DarkNightMAL.user.css
     @Creation-Date      2020-09-18
     @Last-Updated       2021-05-19
    ==/UserStyle== */
    
    /* BODY COLOR */
     body {
         background-color: #0b0024;
         color: #ffffff;
         font-family: Roobert, Helvetica Neue, Helvetica, Arial, sans-serif!important;
    }
    /* BODY DARK THEME COLOR */
     .tw-root--theme-dark body {
         background-color: #0d0021;
    }
    /* Frontpage Carousel Colors */
     .carousel-metadata {
         background: #1a002f;
         cursor: auto;
         width: 22rem;
    }
    .carousel-metadata--fadeout {
         background: linear-gradient(180deg, hsla(0, 0%, 100%, 0) 0, hsl(285deg 93% 48% / 90%) 75%, #ba19f5 80%, #f600ff);
         height: 5rem;
    }
    /* BASE COLORS FOR NAVIGATOR. INCLUDES BUTTON COLOR FOR FOLLOW/UNFOLLOW AND TURN NOTIFICATIONS ON/OFF */
     .tw-c-background-base {
         background-color: rgb(12 0 38) !important;
    }
    /* COLOR FOR BUTTONS SUCH AS PRIME LOOT, NOTIFICATIONS, AND WHISPERS */
     .tw-button-icon {
         background-color: transparent;
         color: rgb(215 25 255);
    }
    /* MAGNIFIER BUTTON */
     .tw-core-button--secondary.tw-core-button--disabled {
         background-color: rgb(76 2 125);
         color: rgb(206 32 255);
    }
    /* SEARCH INPUT WINDOW  */
     .tw-input {
         background-color: rgb(18 3 85);
         border-color: rgb(140 0 249);
         color: rgb(0 255 242);
    }
    .tw-input:focus {
         background-color: rgb(18 3 85);
         border-color: rgb(140 0 249);
         color: rgb(0 255 242);
    }
    /* COLORS FOR BUTTONS SUCH AS: FOLLOWING, BROWSE, ESPORTS, AND MUSIC */
     .navigation-link {
         align-items: center;
         color: #00f5ff !important;
         display: flex;
         text-align: center;
         white-space: nowrap;
    }
    /* DARK THEME ?*/
     .navigation-link {
         align-items: center;
         color: #53006a;
         display: flex;
         text-align: center;
         white-space: nowrap;
    }
    /* DarkTheme */
     .tw-root--theme-dark .navigation-link {
         color: rgb(201 8 255);
    }

    /*============EVERYTHING LEFT NAVIGATION BAR BEGINS============*/
    .wshJJ {
         color: #04dbff  !important;
    }
    /*========LOGGED IN BEGINS========*/
    /* LEFT SIDE NAV COLOR OLD */
     .side-nav__overlay-wrapper {
         background-color: #12012a;
    }
    /*========LOGGED IN ENDS========*/
    /*========LOGGED OUT BEGINS========*/
    /* LEFT SIDE NAV COLOR NEW*/
    .ZkZgD {
         background-color: #12012a  !important;
    }
    /*'Join the Twitch community!' card*/
    .VFufG {
         background-color: #25004d  !important;
    }
    /*========LOGGED OUT ENDS========*/

    /* LEFT SIDEBAR NAVIGATOR BUTTONS: FOLLOWED CHANNELS, RECOMMENDED CHANNELS, AND ONLINE FRIENDS */
     .tw-c-text-alt-2 {
         color: rgb(202 13 255) !important;
    }
    .tw-root--theme-dark .side-nav__overlay-wrapper {
         background-color: #1b0136;
    }
    /*============EVERYTHING LEFT NAVIGATION BAR ENDS============*/


    /* INNER ICON COLOR FOR: FOLLOW/UNFOLLOW AND TURN NOTIFICATIONS ON/OFF */
    .tw-core-button--secondary {
        background-color: #0e0121;
        color: rgb(161 0 233);
    }
    /* COLLOR FOR 'Welcome to the chat room!' MESSAGE AND MODERATION ACTION MESSAGES */
     .chat-line__moderation, .chat-line__status {
         color: rgb(213 0 232);
    }
    /* For Dark Mode */
     .tw-root--theme-dark .chat-line__moderation, .tw-root--theme-dark .chat-line__status {
         color: rgb(213 0 232);
    }
    /* PRIMARY COLOR FOR BUTTONS SUCH AS: SUBSCRIPTE, GIFT A SUB, AND CHAT */
     .tw-core-button--primary {
         background-color: #6700cc;
         color: rgb(5 220 253);
    }
    /* PRIMARY COLOR FOR TEXTS SUCH AS: CHAT TEXT, CHANNEL NAME, STREAM TITEL */
     .tw-c-text-base {
         color: rgb(243 233 233) !important;
    }
    .home-header-sticky, .home-header-sticky__inner, .home-header-sticky__wrapper {
         background: #0e0121 !important;
    }
    /* BACKGROUND COLOR FOR EVERYTHING BEHIND CHANNEL NAME, STREAM TITEL, CHANNEL DISCRIPTION */
     .channel-root__info .channel-info-content {
         background-color: rgb(14 1 33);
    }
    /* TEXT COLOR FOR 'STREAM CHAT' */
     .tw-c-text-alt {
         color: rgb(0 221 255) !important;
    }
    .tw-c-background-alt {
         background-color: #efeff1!important;
         background-color: #0c0026!important;
    }
    .chat-viewers__pane {
         background: #0c0026;
         color: #00d9fb;
    }
    /* BETTER TWITCH TV EXTENSION'S EXTRA EMOTE BUTTON REPLACER */
     #emote-menu-button {
         background-image: url(https://i.imgur.com/HaDlCS9.png) !important;
    }
    /* BETTER TWITCH TV EXENSION'S EMOTE-MENU */
     .emote-menu {
         background-color: rgb(24 1 40);
    }
    .emote-menu .header-info {
         border-top-color: rgb(185 10 255);
         box-shadow: #0778ff 0px 1px 0px inset;
         background-image: linear-gradient(to top, #0d0013, #490ec4);
         color: rgb(0 233 255);
    }
    .emote-menu .footer {
         border-top-color: rgb(11 172 255);
         box-shadow: rgb(0 170 255 / 0%) 0px 1px 0px inset;
         background-image: initial;
         background-color: initial;
    }
    /* TTV SPLIT CHAT COLOR */
     .tw-root--theme-dark .chat-line__message.bttv-split-chat-alt-bg, .tw-root--theme-dark .vod-message.bttv-split-chat-alt-bg {
         background-color: rgb(16 1 29);
    }
    /* dark mode */
     .tw-root--theme-dark .chat-line__timestamp {
         color: #ffffff;
    }
    /* WIthout dark mode */
     .chat-line__timestamp {
         color: #ea00ff;
         margin-right: .5rem;
    }
    /* CHAT BASE COLOR */
     .chat-room {
         background: #06000b;
    }
    .tw-root--theme-dark .chat-room {
         background: #06000b;
    }
    .tw-root--theme-dark .chat-room {
         background: #0d0017;
    }
    /* Input window for chat 'Send a message' */
     .tw-textarea {
         background-color: #0f0020 !important;
         border: var(--border-width-input) solid #012f44 !important;
         color: #ffffff !important;
    }
    /* CHAT MENTION HIGHLIGHT */
     .mention-fragment--recipient {
         background-color: #00f4ff;
         color: #37057e;
    }
    .tw-root--theme-dark .mention-fragment--recipient {
         background-color: #00f4ff;
         color: #37057e;
    }
    /* CHAT TIMESTAMPS */
     .tw-root--theme-dark .chat-room {
         background: #06000b;
    }
    .tw-root--theme-dark .chat-line__timestamp {
         color: #ffffff;
    }
    /* SCREEN CSS */
    
    /* Paused player overlay color */
     .player-overlay-background--darkness-5 {
         background: none;
    }
    /* MOST RECENT VIDEO  */
     .tw-root--theme-dark .chat-room {
         background: #06000b;
    }
    element.style {
         background-color: rgb(22 0 35);
    }
    .tw-root--theme-dark .scrollable-area .simplebar-track .simplebar-scrollbar {
         background: #03fff4;
    }
    /* TAGS */
     .tw-tag {
         background-color: #290038;
         color: rgba(0, 0, 0, .7);
         color: #02fffe;
    }
    /* HEADER BANNER COVER COLOR SUCH AS ON /game/Music page */
     .directory-header-new__banner-cover {
         background: linear-gradient(0deg, #500373, rgb(3 3 255 / 0.25)), linear-gradient(90deg, #03ddff, rgb(246 0 255 / 0.25));
         content:"";
    }
    /* FOR DARK THEME */
     .tw-root--theme-dark .directory-header-new__banner-cover {
         background: linear-gradient(0deg, #500373, rgb(3 3 255 / 0.25)), linear-gradient(90deg, #03ddff, rgb(246 0 255 / 0.25));
         content:"";
    }
    .tw-image-avatar {
    
    }
    .tw-border-t {
         border-top: 1px solid rgba(0, 0, 0, .1)!important;
         border-top: var(--border-width-default) solid hsl(191deg 100% 50%)!important;
    }
    .tw-border-l {
         border-left: 1px solid rgba(0, 0, 0, .1)!important;
         border-left: var(--border-width-default) solid hsl(193deg 100% 50% / 10%)!important;
    }
    .chat-line__message.bttv-split-chat-alt-bg, .vod-message.bttv-split-chat-alt-bg {
         background-color: #19004f;
    }
    .tw-form-label {
         color: #18181b;
         color: #05f5ff;
    }
    .tw-select-button {
         color: #05edfb;
    }
    element.style {
         color: #06f5ff;
    }
    
    /*NEW CHANGES*/
    
    /*Follow and notification buttons*/
    .KNLSb {
         background-color: #6f00d9 !important;
         }
    
    /*Follow and notification icons*/
    .ivDpGz {
         color: #04dbff !important;
    }
    
    /*Subscribe and Gift a Sub button and icon*/
    .jbpuQw {
         background-color: #6f00d9 !important;
         color: #04dbff !important;
    }
    
    /*User notice chat message for subscribe anniversaries*/
    .qfuHB {
         background-color: #25004d !important;
    }
    
    .bVXJYZ {
         background-color: #0c0026 !important;
    }
    
    /*Channel Leaderboard header, where bits and gifted sub leaders are*/
    .goGBPp {
         background-color: #0c0026 !important;
    }
    
    /*Extended Channel Leaderboard*/
    .drMKSw {
         background-color: #0c0026 !important;
    }
    
    /*Extended leaderboard minimize button*/
    .dhMLHp {
         background-color: #04dbff !important;
    }
    
    /*'STREAM CHAT' header*/
    .jGtCgK {
         background-color: #0c0026 !important;
    }
    /*'STREAM CHAT' text*/
    .hQRdId {
         color: #04dbff !important;
    }
    
     /*==========General Icons Begins==========*/
     /*=======LOGGED IN BEGINS========*/
    /*Icons such as Inbox, Whisper, Bits, New Prime Loot, Share, Emotes Chat settings etc.*/
    .hSxEIC {
         color: #09effa !important;
    }
     /*'Click to claim a bonus!' button, basically channel points chest*/
    .VGQNd {
     background-color: #6f00d9 !important;
     color: #09effa !important;
     }
     /*=======LOGGED IN ENDS========*/
     /*=======LOGGED OUT BEGINS========*/
     /*General icons*/
    .cMQeyU{
          color: #09effa !important;
    }
    /*Follow heart and text*/
    .xsINH {
         color: #04dbff !important;
    }
     /*=======LOGGED OUT ENDS========*/
     /*==========General Icons Ends==========*/

    
    /*Default Channel Points icon*/
    .gShCOc {
         color: #09effa !important;
    }
    
    /*Channel Points text*/
    .GBpnX {
         color: #09effa !important; 
    }
    
    /*Top Navigation bar with buttons such as: 'Following', 'Browse', and the search bar.*/
    .cPVNOL {
         background-color: #0c0026 !important;
    }
    
    /*Profile Picture background on the top right of the navigation bar*/
    .hDxvGD {
         background-color: #0c0026 !important;
    }
    
    /*Friends activity status, Followed channels category text, and new videos on the left navigator bar.*/
    .cbThWs {
         color: #f66bff !important;
    }
    
    /*Followed Channels, and Online Friends usernames*/
    .eAXpco {
         color: #04dbff !important;
    }
    
    /*Followed Channels Viewer Count and */
    .cbThWs {
         color: #f66bff  !important;
    }
    
    /*Get Bits button*/
    .iDPpfj {
         background-color: #6f00d9 !important;
         color: #04dbff !important;
    }
    .iDPpfj:hover {
         background-color: #8a10fe !important;
    }
    
    .highlight {
         background-color: #25004d !important;
    }
    
    /*Magnifyer icon and search button on top navigator bar*/
    .fMfcuC {
         background-color: #3500a7 !important;
         color: #04dbff !important;
    }
    /*=====EVERYTHING DROPDOWN MENU PROFILE PICTURE=======*/
    /*Status such as Offline, Online, and away*/
    .presence-text {
         color: #a749fd !important;
    }
    /*Status dot Offline*/
    .jrLemK {
         background-color: #6f44a9;
    }
    /*Status dot Online*/
    .gFaMzg {
         background-color: #00eaf5 !important;
    }
    /*Dropdown menu from Profile Picture button*/
    .bXPPWD {
         background-color: #110035  !important;
    }
    /*Dropdown menu text*/
    .llxjvH {
         color: #04dbff  !important;
    }
    .gzOMdE {
         color: #04dbff  !important;
    }
    .eEFwBT {
         color: #04dbff  !important;
    }
    .dENtHr {
         color: #d6c929  !important;
    }
    /*Toggle button inside profile dropdown menu disabled*/
    .oHwVV {
         background:  #6f00d9 !important;
         border-color: #00ddff !important;
    }.oHwVV:hover {
     border-color: #00ddff !important;
     }
    .oHwVV::after {
         background-color: #04dbff !important;
    }

    /*Primeloot notification count*/
    .ibisXf {
         background-color: #6f00d9 !important;
         color: #04dbff !important;
    }
    
    /*Bottom left searchbar background on left navigator menu*/
    .eqpKSV {
         background-color: #1b0136 !important;
    }
    
    /*Chat Settings pop-up menu*/
    .ixPmjS {
         background-color: #04dbff  !important;
         color: #0c0026  !important;
    }
    
    /*Top header in Chat Settings pop-up menu*/
    .yjoEf {
         background-color: #0c0026  !important;
    }
    /*Top header text in Chat Settings pop-up menu*/
    .chUZlO {
         color: #04dbff  !important;
    }
    .dVuSXW {
         color: #7207ff  !important;
    }
    .tw-c-text-alt-2 {
         color: #7207ff !important;
    }
    
    /*Purchase Bits dropdown menu*/
    .iTdjRF {
         background-color: #0c0026  !important;
    }
    
    /*Whispers dropdown menu*/
    .rWdm {
         border-bottom: #00aeff !important;
    }
    .rWdm:hover {
         background-color: #25004d !important;
    }
    
    .whispers-list-item:hover .whispers-list-item__archive, .whispers-list-item:hover .whispers-list-item__visible-icons {
         background-color: #0c0026 !important;
         border-bottom-color: #04dbff !important;
    }
    
    
    .tw-root--theme-dark .whispers-list-item--selected .whispers-list-item__archive-button, .tw-root--theme-dark .whispers-list-item:hover .whispers-list-item__archive-button {
         background-color: #0c0026 !important;
    }
    
    .jKbvEr {
         border-bottom-color: #04dbff !important;
         background-color: #0c0026 !important;
    }
    
    
    /*========CHANNEL PAGE BEGINS=========*/
    /*LOGGED IN BEGINS*/
    /*Channel info toggled*/
    .hVRTjq {
         background-color: #25004d !important;
    }
    /*channel status info offline (the offline/online thing above the username*/
    .channel-status-info--offline, .channel-status-info--private {
         background: #6f00d9 !important;
         color: #04dbff !important;
    }
    /*'is offline.' text*/
    .jBeHoW {
         color: #04dbff !important;
    }

    
    
}
