css:
@-moz-document url-prefix("https://vk.com") {
/* VKhead v2.1 [13.05.2021 @ 11:00]*/


/* ЦВЕТОВАЯ СХЕМА */
/*[[colorinf]]*/


/* СОБСТВЕННО, ШАПКА */
body.new_header_design {--header-height: 42px !important;}
#page_header_cont {
	background-color: var(--custcolor-main) !important;
	border-bottom: 1px solid var(--custcolor-main) !important;}
[dir] .top_nav_link {padding: 1px 10px;}


/* ТЕНЬ ШАПКИ */
#page_header_cont { box-shadow: /*[[headshdw]]*/ !important; }


/* ЛОГОТИП */
/*[[logostyle]]*/


.top_home_link .top_home_logo, .TopHomeLink--ny2021logo:not(.TopHomeLink--logoWithText), [dir] body.WideScreenAppPage .TopHomeLink--ny2021logo, [dir] .TopHomeLink {
	background: var(--custlogo_img) !important;
	background-size: auto 30px;
	background-repeat: no-repeat !important;
	height: var(--custlogo_w) !important;}
.TopHomeLink svg {display: none !important;}


/* КНОПКА ДЕЙСТВИЯ СЛЕВА ОТ ЛОГОТИПА */
[dir] body.new_header_design .WideScreenAppBack {
	margin-left: -10px;
	background-color: var(--custcolor-main);
	padding: 0px 20px 0 16px;}
body.new_header_design .WideScreenAppBack__link {
	color: #dae3ed;
	font-weight: 500;}
[dir="ltr"] .WideScreenAppBack__link {
	padding: 0px 10px 1px 2px;
	margin-left: -10px;}
.WideScreenAppBack__arrowLeft {
	filter: brightness(1.5) !important;}
[dir] body.new_header_design .WideScreenAppBack__link:hover {
	background-color: #00143333 !important;
	filter: brightness(2);}


/* ПОИСК */
.TopSearch__input {
	background-color: var(--custcolor-search) !important;
	background-image: url(http://vk.com/images/svg_icons/ic_head_loupe.svg) !important;
	background-position-x: 11px;
	border-radius: 8px !important;
	height: 28px !important;}
.TopSearch .input_back_content {color: #86a1bf !important;}
.TopSearch__input, .TopSearch__input::placeholder {color: #becbd9 !important;}
#ts_cont_wrap::before {
	position: absolute;
	border: solid transparent;
	border-bottom-color: #fffffe !important;
	display: block !important;
	border-width: 7px !important;
	margin: 0 -9px !important;}

	
/* ЦЕНТР УВЕДОМЛЕНИЙ */
.TopNavBtn__icon {filter: brightness(5.0) !important;}
.TopNavBtn__notifyCount {border: 2px solid var(--custcolor-main) !important;}
.TopNavBtn:hover {background-color: var(--custcolor-hover) !important;}
.TopNavBtn:hover .TopNavBtn__notifyCount::after {border-color: var(--custcolor-hover) !important;}
[dir] .TopNavBtn.TopNavBtn--active {background-color: var(--custcolor-active) !important;}
#top_notify_btn.has_notify .TopNavBtn__notifyCount {display: block; background-color: #FF734D !important; border-radius: 10px !important;}
#top_notify_wrap::before {content: '' !important; border-bottom-color: #fffffe !important; display: block !important; border-width: 7px !important; margin: 0 -9px !important;}
#top_notify_count {
	font-size: 10.5px !important;
	line-height: 13px !important;
	font-weight: 500 !important;}

/* ПЛЕЕР */
.top_audio_player:hover {background-color: var(--custcolor-hover) !important;}
.top_audio_player_btn.top_audio_player_prev {padding-left: 10px !important;}
.top_audio_player .top_audio_player_btn {
	float: left !important;
	filter: brightness(3.0) !important;
	margin: -1px !important;}
body.new_header_design .top_audio_player_prev, body.new_header_design .top_audio_player_next, body.new_header_design .top_audio_player_play {margin: -2px !important;}
body.new_header_design .top_audio_player_title_wrap {padding-left: 6px !important;}
.top_audio_player_title {color: #becbd9 !important;}
body.new_header_design .top_audio_layer_place .eltt_arrow_back {content: '' !important; display: block !important;}
#ts_cont_wrap {margin-top: 1px;	border-top: none;}


/* ПАНЕЛЬ УПРАВЛЕНИЯ АККАУНТОМ И ДРУГИЕ ДЕЙСТВИЯ СПРАВА */
.TopNavBtn__profileLink:hover, .top_nav_btn:hover, .top_switch_lang:active {
	background-color: var(--custcolor-hover) !important;
	color: #fff !important;}
.TopNavBtn__profileName:active, .top_switch_lang:active {color: #fff !important;}
#top_profile_link.active, #top_switch_lang.active {background-color: var(--custcolor-active) !important;}
.TopNavBtn__profileName, .top_switch_lang {color: #e4ebf0 !important;}
.TopNavBtn__profileName.active {color: #ffffff !important;}
.top_profile_mrow {color: #2a5885;}
#top_profile_menu::before {content: '' !important; display: block !important; border-width: 8px !important; margin: 0 -9px !important;}
#top_profile_menu::after {content: '' !important; display: block !important; border-width: 7px !important; margin: 0 -8px !important;}
#top_profile_menu {margin-top: 1px;	border-top: none !important;}
body.new_header_design .top_nav_link, .TopNavBtn__profileArrow {color: #e4ebf0 !important;}
body.new_header_design:hover .top_nav_link:hover {color: #fff !important;}
[dir] .top_nav_link:hover {background-color: var(--custcolor-hover) !important;}


/* УВЕДОМЛЕНИЯ В САЙДБАРЕ */
[dir] .left_count_wrap {background: var(--blue_400_alpha48) !important;}
.left_count_wrap.fl_r.left_count_wrap_hovered:hover {text-decoration: none !important;}
}
