css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Last Modified on Feb 9, 2013. 
 * See Change log. http://userstyles.org/styles/19974/ */


@-moz-document url("http://www.yahoo.co.jp/") {
/* 最上部ヘッダー非表示 */
#header>#hdBar,
/* 検索ボックス下広告非表示 */
#contents>#toptxt,
/* 左カラム全非表示 */
#contents>#navi>*,
/* 中央カラム全非表示 */
#contents>#division>#main>*,
/* 右カラム全非表示 */
#contents>#division>#sub>*{
  display:none;
}

/* 左カラム サービス一覧を表示 */
#contents>#navi>#contentbox,
/* 中央カラム Yahoo!Newsを表示 */
#contents>#division>#main>#topicsbox,
/* 右カラム パーソナルボックスを表示 */
#contents>#division>#sub>#personalbox
/* 右カラム Facebook通知を表示 */
/*[[Facebook]]*/{
  display:block;
}

/* オンマウス広告枠の非表示 */
[id^="ADframe"],
/* 背面広告の非表示 */
#topImpact,
/* 右カラム下iframe広告の非表示 */
#contents>#division>#sub>iframe{display:none !important;}

/*カレンダー設定*/
/*[[NeedCalendar]]*/

/* ヘッダー高さ修正 */
#masthead{
  margin-top:0.5em !important;
}

/* 右カラム パーソナルボックスの編集 */
#contents>#division>#sub>#personalbox>#pbsocial,
#contents>#division>#sub>#personalbox>#pbalert,
/* ヘッダーの編集 */
#masthead>#siteinfo,
#masthead>#changeMode{
  display:none;
}

/* Yahoo!Service Menu一覧の編集 - 高さ修正 */
#contentbox li{
  line-height:1.7 !important;
  padding:0 !important
}

/* Yahoo!Service Menu一覧の編集 - 必要なものはコメントアウトする */

/* Service Menu内のNewアイコン */
#contentbox .iconNew{display:none !important;}

/* Service Menu内のリンクの下に表示されるプロモーションメッセージ */
#contentbox>#yahooservice>ul>li>a+ul{display:none !important;}

/* リンクに当たっているpaddingを無効化 */
#contentbox ul li a{padding:0 0 0 20px !important;}
/* ショッピング */
/*[[Y!shopping]]*/

/* オークション */
/*[[Y!auction]]*/

/* 日用品宅配 */
/*[[lohaco]]*/

/* 旅行、出張 */
/*[[Y!travel]]*/

/* ニュース */
/*[[Y!news]]*/

/* 天気 */
/*[[Y!weather]]*/

/* スポーツ */
/*[[Y!sports]]*/

/* ファイナンス */
/*[[Y!finance]]*/

/* テレビ */
/*[[Y!tv]]*/

/* ゲーム */
/*[[game]]*/

/* Gyao! */
/*[[gyao!]]*/

/* Y!モバゲー */
/*[[Y!mobage]]*/

/* 地図 */
/*[[Y!map]]*/

/* 路線 */
/*[[Y!transit]]*/

/* グルメ */
/*[[Y!gourmet]]*/

/* 求人 */
/*[[Y!recnavi]]*/

/* 不動産 */
/*[[Y!realestate]]*/

/* 自動車 */
/*[[Y!autos]]*/

/* 掲示板 */
/*[[Y!messages]]*/

/* ブログ */
/*[[Y!blog]]*/

/* 服、ビューティー */
/*[[Y!beauty]]*/

/* 出会い */
