/* お気に入りラベル */
#favoriteservice .hd,

/* 電子書籍 */
#favoriteservice a[href="r/cf3"],

/* 映画 */
#favoriteservice a[href="r/cf17"],

/* 音楽 */
#favoriteservice a[href="r/cf18"],

/* ゲーム */
#favoriteservice a[href="r/cf20"],

/* 占い */
#favoriteservice a[href="r/cf21"],

/* 動画 */
#favoriteservice a[href="r/cf26"],

/* ピックアップ */
#pickupservice{
  display:none !important;
}

}
