css:
@-moz-document domain("wolai.com") {
/* www.Wolai.com自定义样式代码 */
/*版本时间 2021年5月6日22:11:49*/
/* 暗夜模式下 */
html[theme='dark'] body {
	/* background: #2F3437;*/
	background: #202020;
	/*暗夜模式下主编辑区背景色 */
}
:root[theme='dark'] {
	--wolai-sidebar-bg: #000000;
	/* 暗夜模式下左边栏背景色 */
	/*  --wolai-page-cover-default-bg: #2F3437;暗夜模式题图区背景色 */
	--wolai-editor-bg: #202020;
	/* 暗夜模式题图区背景色 */
	--wolai-bi-link-text-color: #FE7505;
	/* 暗夜模式下页标题引用文字颜色 */
	--wolai-bi-link-text-bg: #454545;
	/* 暗夜模式下页标题引用部分背景颜色 */
	--wolai-sidebar-tree-node-color: #ABABAB;
	--wolai-code-cdata: #6c757d;
	/* 黑色模式代码注释文字颜色 */
	--wolai-border-color: rgba(153, 153, 153, 0.39);
	/* 黑色模式虚线框的颜色 */
	--wolai-modal-bg: rgb(0, 0, 0);
	/* 黑色模式下骰子菜单背景颜色 */
	--wolai-code-node-bg: rgba(90, 90, 90, 0.5);
	/* 黑色模式下代码片段背景颜色 */
	--wolai-link-node-color: rgba(198, 197, 196, 0.93);
	/* 黑色模式下link文字颜色 */
	--wolai-link-node-line-color: rgba(225, 225, 225, 0.2);
	/* 黑色模式下link文字下划线颜色 */
	--wolai-link-node-hover-color: rgba(225, 225, 225, 0.7);
	/* 黑色模式下link鼠标滑动上面时文字颜色 */
	--wolai-bookmark-hover-color: rgb(0, 0, 0)/*黑色模式下网址书签滑动上面时背景颜色 */
}
/* 白天模式代码注释文字颜色 */
/* 白天模式下 */
html body {
	background: #ffffff;
	/* 白天模式下主编辑区背景色 */
}
:root {
	/* --wolai-sidebar-bg: #E7E0D2;   */
	--wolai-bi-link-text-color: #FE7505;
	/* 白天模式页标题引用文字颜色*/
	--wolai-bi-link-text-bg: #FAFAFA;
	/* 白天模式页标题引用部分背景颜色 */
	--wolai-page-cover-default-bg: #ffffff;
	/* 白天模式题图区背景色 */
	--wolai-editor-bg: #ffffff;
	/* 白天模式题图区背景色 */
	--wolai-code-cdata: #9c9e9f;
	/* 白天模式代码注释文字颜色 */
	--wolai-border-color: #cccccc;
	/* 白色模式虚线框的颜色 */
	--wolai-code-node-bg: #eff1f1;
	/* 白色模式下代码片段背景颜色 */
	--wolai-bookmark-hover-color: rgba(232, 232, 232, 0.24)/* 白色模式下网址书签滑动上面时背景颜色 */
}

._2WQtZ {
	width: 100%;
	white-space: pre-wrap;
	outline: none;
	border: 1px dashed var(--wolai-border-color);
	border-radius: 0px;
	/* 虚线框圆角半径 */
	padding: 3px 2px;
	box-sizing: border-box;
	-webkit-transition: border-color 220ms;
	transition: border-color 220ms;
}
/* ↓文字层级标识线 */
/*↑文字层级标识线 */
._2hMpN {
	text-align: center;
	/*设置图片说明文字水平居中*/
}
._2QRuO {
	border-radius: 6px;
	/*设置图片圆角半径*/
}
._1N86P ._1YruY {
	display: none;
}
::-webkit-scrollbar {
	display: none;
	/* Chrome Safari */
}
/*引述文字*/
._3Z5FW {
	background-color: #179ED9;
	/*引述文字左边竖线颜色*/
	width: 0px;
	/*引述文字左边竖线条宽度*/
	border-radius: 3px;
	/*引述文字左边竖线条四角半径*/
	position: absolute;
	left: 4px;
	top: 6px;
	height: calc(100% - 12px);
}
html .W_9-O {
	/*background-color:rgba(220,220,220,0.3);/*引述文字背景颜色*/
	font-size: 16px;
	/*引述文字字号*/
	letter-spacing: 1px;
	/*引述文字间距*/
	border-radius: 6px;
	/*引述文字背景边框圆角半径*/
	padding-left: 1px !important;
	padding-top: 3px !important;
	padding-bottom: 3px !important;
	padding-right: 1px !important;
	/*min-height: 50px !important;*/
	display: -webkit-box;
	display: flex;
	text-align: justify;
	text-align-last: left;
}
.W_9-O:before {
	/*引述文字加入双引号*/
	content: '“';
	font-size: 50px;
	color: rgba(200, 200, 200, 0.8);
	/*font-weight:bolder;*/
	align-self: flex-start !important;
	margin-left: -12px;
	margin-top: -10px;
	margin-bottom: -30px;
}
.W_9-O:after {
	/*引述文字加入双引号*/
	content: '”';
	font-size: 50px;
	/*font-weight:bold;*/
	color: rgba(200, 200, 200, 0.8);
	align-self: flex-end !important;
	margin-left: 0px;
	margin-right: -6px;
	margin-bottom: -30px;
}

.node-container {
	border-radius: 6px !important;
	/* 带背景色块的边框圆角半径 */
}
