._2TL9K {
	outline: none;
	text-align: left;
	font-size: 16px;
	min-width: 0px;
	height: 100%;
	width: 100%;
	font-family: JetBrainsMono-Regular, SFMono-Regular, Consolas, 'Liberation Mono', Menlo, monospace;
	font-family: 'Consolas'!important;
	/*代码片段字体和大小*/
	color: var(--wolai-code-node-color);
}
._2ms-i {
	border-radius: 6px;
	/*代码区背景边框圆角半径*/
	border: 2px solid var(--wolai-border-color);
	text-align: left;
	background: var(--wolai-code-node-bg) !important;
	/*代码区背景颜色，颜色见24和46行*/
	min-width: 0px;
	min-height: 42px;
	/*代码区最小高度*/
	width: 100%;
	padding: 15px;
	/*代码区边框度*/
	display: -webkit-box;
	display: flex;
	-webkit-box-orient: horizontal;
	-webkit-box-direction: normal;
	flex-direction: row;
	overflow-x: auto;
}
._2VNwp.x_EoR {
	text-decoration: none;
	/*待办勾选后的样式无划线*/
	opacity: 0.375;
	caret-color: var(--wolai-desc-color);
}


/*↓-------附件样式------↓*/
._1dtSv {
	/*文件下载块样式*/
	background-color: rgba(177, 177, 177, 0.1);
	-webkit-box-flex: 1;
	flex: 1;
	border-radius: 6px;
	overflow: hidden;
	position: relative;
	border: 2px solid rgba(200, 200, 200, 0.24) !important;
}
._1dtSv:not(.ApPbZ):hover {
	/*文件下载块hover样式*/
	background-color: rgba(200, 200, 200, 0.33);
}
._1dtSv ._1IVHw:hover ._6Uq1e svg path {
	/*文件下载块hover预览图标样式*/
	color: #cf5659;
	fill: #cf5659;
	stroke-width: 2px;
}
/*↑-------附件样式------↑*/

._3PC7s {
	/*页关系图样式包括宽度，背景色等*/
	width: 800px;
	box-shadow: rgba(15, 15, 15, 0.05) 0px 0px 0px 1px, rgba(15, 15, 15, 0.1) 0px 3px 6px, rgba(15, 15, 15, 0.2) 0px 9px 24px;
	height: 800px;
	background-color: var(--wolai-modal-bg);
	border-radius: 6px;
}

.QP3W5 path {
	/*右下角帮助中的按钮图标颜色*/
	fill: #6606f4;
}

span[data-biblock-type="progressBar"] {
	/*行内进度条引用*/
	color: var(--wolai-caret-color) !important;
	background-color: rgba(200, 200, 200, 0.2)!important;
	padding: 1px 6px 2px 8px !important;
	margin: 1px 5px 1px 6px;
	border-radius: 12px !important;
	cursor: pointer;
}
span[data-biblock-type="progressBar"]:hover {
	background-color: rgba(200, 200, 200, 0.4)!important;
}
span[data-biblock-type="progressBar"]:after {
	content: "完";
	/*行内进度条引用*/
	font-size: 10px !important;
	font-weight: 500;
	border-radius: 12px;
	border: 0px dotted #ff0018 !important;
	color: rgba(200, 200, 200, 0.6) !important;
	background-color: var(--wolai-editor-bg) !important;
	margin-right: -4px !important;
	margin-left: 4px;
	padding: 1px 4px!important;
	visibility: hidden;
	opacity: 0;
	transition: opacity 1s;
}
span[data-biblock-type="progressBar"]:hover:after {
	visibility: visible;
	/*普通页 提示信息*/
	opacity: 1;
}
/*行内块引用*/
._2cdhz ._2E21Z {
	border: 2px solid var(--wolai-bookmark-node-border-color);
	border-radius: 12px;
	/*网址书签圆角半径*/
	overflow: hidden;
	min-height: 50px;
}
._219hV ._1LO_D img {
	/*网页书签图片样式*/
	width: 96px;
	height: 100%;
	float: right;
	object-fit: cover;
	border: 0px solid #1ce6d4;
	padding: 5px;
	border-radius: 12px;
}
._2cdhz:hover {
	/* 网址书签滑动上面时背景颜色透明 */
	background-color: rgba(192, 20, 20, 0);
}
._219hV {
	/*网址书签默认样式*/
	width: 100%;
	min-height: 50px;
	max-height: 120px;
	line-height: 19px;
	background-color: rgba(255, 255, 255, 0);
	display: -webkit-box;
	display: flex;
	-webkit-box-orient: horizontal;
	-webkit-box-direction: normal;
	flex-direction: row;
	-webkit-box-pack: justify;
	justify-content: space-between;
}
._219hV:hover {
	/*网址书签鼠标滑动时背景颜色样式*/
	background-color: var(--wolai-bookmark-hover-color);
}
._219hV ._3KJyU ._19s1q ._1hU5R {
	/*网址书签内文字的样式*/
	display: block;
	/* or inline-block */
	text-overflow: ellipsis;
	word-wrap: break-word;
	overflow: hidden;
	max-height: 20px;
	color: var(--wolai-bookmark-description-color);
	flex-shrink: 0;
	-webkit-box-flex: 0;
	flex-grow: 0;
	font-size: 10px;
}
._219hV ._3KJyU {
	width: 70%;
	/*网址书签内内容介绍文字的宽度样式*/
	padding-left: 16px;
	padding-top: 16px;
	padding-bottom: 16px;
	padding-right: 5px;
	color: var(--wolai-bookmark-title-color);
	display: -webkit-box;
	display: flex;
	-webkit-box-orient: vertical;
	-webkit-box-direction: normal;
	flex-direction: column;
	-webkit-box-pack: justify;
	justify-content: space-between;
}
._219hV ._1LO_D {
	width: 100px;
	/*网址书签内图片宽度样式*/
	position: relative;
	overflow: hidden;
}
/*↓-------按钮模板样式------↓*/
._3fusM:hover ._26KFI:hover {
	background-color: rgba(200, 199, 199, 0.2);
	/*模板按钮鼠标滑动上面底色*/
}
._3fusM ._2wdS- {
	border-radius: 9px;
	/*模板按钮底色边框圆角*/
}
._3fusM ._2wdS- ._2zCCk {
	/*模板按钮圆角*/
	border: 2px solid rgba(158, 156, 156, 0.55);
	border-radius: 6px;
	padding: 3px 6px;
}
.EG72n svg {
	width: 25px;
	height: 25px;
	padding: 0px 1px 0px 0px;
}
.EG72n path {
	/*按钮图标加号大小和颜色*/
	stroke-width: 2px;
	stroke: rgba(158, 156, 156, 0.55);
	fill: rgba(158, 156, 156, 0.55);
}
._3fusM ._2wdS- ._3--Y7 {
	padding: 12px 1px 0px 0px;
