@license        CC0-1.0
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("wolai.com") {
	/*-------------------------------------------「Wolai StylePlus」目录区------------------------20210511----------------------------*/
	/*-------暗色主题下一些颜色设置------*/
	/*-------白色主题下一些颜色设置------*/
	/*-------左侧边栏------*/
	/*-------虚线框------*/
	/*-------色块圆角------*/
	/*-------页面关系图------*/
	/*-------右下角帮助------*/
	/*-------小号字体------*/
	/*-------文字层级线------*/
	/*-------块文字居中居右------*/
	/*-------图片------*/
	/*-------腾讯视频播放器-----*/
	/*-------引述------*/
	/*-------着重------*/
	/*-------模板按钮------*/
	/*-------代码片段------*/
	/*-------附件------*/
	/*-------网址书签------*/
	/*-------数字标------*/
	/*-------引用信息------*/
	/*-------行内进度条引用------*/
	/*-------待办列表------*/
	/*-------行内代码------*/
	/*-------网址链接------*/
	/*-------页面标题------*/
	/*-------行内页引用------*/
	/*-------行内块引用------*/
	/*-------嵌入页引用------*/
	/*-------文字批注------*/
	/*↓-------标题目录------↓*/
	/*-------------------------------------------以下是代码区----------------------------------------------------*/
	/*-------@暗色主题下一些颜色设置------*/
	:root[theme='dark'] {
		--wolaistyleplus-editor-bgcolor: #121212;
		/*编辑区背景色*/
		--wolaistyleplus-page-hover-bgcolor: #cf5659;
		/*页标题hover背景色*/
		--wolaistyleplus-page-inline-hover-bgcolor: #458994;
		/*行内引用页标题hover背景色*/
		--wolaistyleplus-page-embed-hover-bgcolor: #539d6e;
		/*嵌入页引用页标题hover背景色*/
		--wolaistyleplus-block-inline-hover-bgcolor: #7c59b0;
		/*行内块引用hover背景色*/
		--wolaistyleplus-block-inline-hover-textcolor: #7c59b0;
		/*行内块引用hover文字颜色*/
		--wolaistyleplus-block-inline-bgcolor: rgba(200, 200, 200, 0.2);
		/*行内块引用背景色*/
		--wolaistyleplus-page-bgcolor: rgba(200, 200, 200, 0.2);
		/*页标题背景色*/
		--wolaistyleplus-page-inline-bgcolor: rgba(200, 200, 200, 0.2);
		/*行内引用页标题背景色*/
		--wolaistyleplus-page-embed-bgcolor: rgba(200, 200, 200, 0.2);
		/*嵌入页引用页标题背景色*/
		--wolaistyleplus-number-notes-bgcolor: #3F3F3F;
		/*数字标点击之后引用区背景色*/
		--wolaistyleplus-reference-zone-bgcolor: rgba(113, 113, 113, 0.23);
		/*页面下部引用区背景色*/
		--wolaistyleplus-left-Selected-bgcolor: #3b3a3a;
		/*左侧边栏被选中的页标题背景色 */
		--wolaistyleplus-code-inline-bgcolor: rgba(200, 200, 200, 0.3);
		/*行内代码背景色*/
		--wolaistyleplus-code-inline-color: #179ed9;
		/*行内代码文字颜色*/
		--wolaistyleplus-code-inline-notecolor: rgba(200, 200, 200, 0.5);
		/*行内代码符号标颜色*/
		--wolai-code-node-bg: rgba(90, 90, 90, 0.5);
		/* 黑色模式下代码片段背景颜色 */
		--wolai-bookmark-hover-bgcolor: #9c9e9f;
		/*黑色模式下网址书签滑动上面时背景颜色 */
		--color-basic-75: #202020;
		/* 暗夜模式下左边栏背景色 */
		--wolai-editor-bg: #121212;
		/* 暗夜模式题图区背景色 */
		--wolai-sidebar-tree-node-color: #9c9e9f;
		/* 左侧页标题文字hover颜色 */
		--wolai-border-color: rgba(200, 200, 200, .2);
		/* 黑色模式虚线框的颜色 */
		--wolai-modal-bg: #3f3f3f;
		/* 黑色模式下骰子菜单背景颜色 */
		--wolaistyleplus-code-inline-note-bgcolor: var(--wolai-editor-bg);
		/*背景色*/
	}
	/*↑-------暗色主题下一些颜色设置------↑*/
	/*------------------------------------------------------------------------------------------------------------------*/
	/*-------@白色主题下一些颜色设置目录------*/
	:root {
		--wolaistyleplus-editor-bgcolor: #ffffff;
		;
		/*编辑区背景色*/
		--wolaistyleplus-page-hover-bgcolor: #fc9395;
		/*页标题hover背景色*/
		--wolaistyleplus-page-inline-hover-bgcolor: #6dd5e5;
		/*行内引用页标题hover背景色*/
		--wolaistyleplus-page-embed-hover-bgcolor: #95f6b8;
		/*嵌入页引用页标题hover背景色*/
		--wolaistyleplus-block-inline-hover-bgcolor: #bc93f8;
		/*行内块引用hover背景色*/
		--wolaistyleplus-block-inline-hover-textcolor: #bc93f8;
		/*行内块引用hover字标文字颜色*/
		--wolaistyleplus-block-inline-bgcolor: rgba(200, 200, 200, 0.2);
		/*行内块引用背景色*/
		--wolaistyleplus-page-bgcolor: rgba(200, 200, 200, 0.2);
		/*页标题背景色*/
		--wolaistyleplus-page-inline-bgcolor: rgba(200, 200, 200, 0.2);
		/*行内引用页标题背景色*/
		--wolaistyleplus-page-embed-bgcolor: rgba(200, 200, 200, 0.2);
		/*嵌入页引用页标题背景色*/
		--wolaistyleplus-number-notes-bgcolor: #F8F8F8;
		/*数字标点击之后引用区背景色*/
		--wolaistyleplus-reference-zone-bgcolor: rgba(232, 232, 232, 0.23);
		/*页面下部引用区背景色*/
		--wolaistyleplus-left-Selected-bgcolor: #ffffff;
		;
		/*左侧边栏被选中的页标题背景色 */
		--wolaistyleplus-code-inline-bgcolor: rgba(200, 200, 200, 0.3);
		/*行内代码背景色*/
		--wolaistyleplus-code-inline-color: #179ed9;
		/*行内代码文字颜色*/
		--wolaistyleplus-code-inline-notecolor: rgba(200, 200, 200, 0.5);
		/*行内代码符号标颜色*/
		--wolaistyleplus-code-inline-note-bgcolor: var(--wolai-editor-bg);
		/*背景色*/
		--color-basic-75: #F9F7F6;
		/* 白天模式下左边栏背景色 */
		--wolai-editor-bg: #fff;
		/* 白天模式题图区背景色 */
		--wolai-border-color: #cccccc;
		/* 白色模式虚线框的颜色 */
		--wolai-sidebar-tree-node-color: #606466;
		/* 左侧页标题文字hover颜色 */
		--wolai-code-node-bg: #eff1f1;
		/* 白色模式下代码片段背景颜色 */
		--wolai-bookmark-hover-bgcolor: rgba(232, 232, 232, 0.24);
		/* 白色模式下网址书签滑动上面时背景颜色 */
	}
	/*↑-------白色主题下一些颜色设置------↑*/
	/*------------------------------------------------------------------------------------------------------------------*/
	html body {
		background: var(--wolaistyleplus-editor-bgcolor) !important;
		/* 主编辑区背景色 */
	}
	/*-------@左侧边栏------*/
	.treeBeanSelected {
		background-color: var(--wolaistyleplus-left-Selected-bgcolor) !important;
		/*白色模式下左侧边栏被选中的页标题背景色 */
	}
	._14df9 ._3pAxv {
		color: var(--wolai-svg-CF5659);
		/*左侧目录中鼠标hover的文字色*/
	}
	._3o1Po:not(._2AVeS):hover {
		background-color: var(--wolai-sidebar-tree-node-selected-bg);
		/*左侧目录中鼠标hover的背景色*/
	}
	._3o1Po ._4T5BJ path {
		color: var(--wolai-svg-CF5659);
		fill: var(--wolai-svg-CF5659);
		/*左侧目录中>符号颜色*/
	}
	i[style="font-size: 18px; color: rgb(207, 86, 89);"] {
		color: var(--wolai-svg-CF5659) !important;
		/*图标是fa格式时，选中的目录图标颜色*/
	}
	/*↑-------左侧边栏------↑*/
	/*------------------------------------------------------------------------------------------------------------------*/
	/*-------@虚线框------*/
	._2WQtZ {
		border-radius: 0px;
		/* 虚线框圆角半径 */
	}
	/*↑-------虚线框------↑*/
	/*------------------------------------------------------------------------------------------------------------------*/
	/*-------@色块圆角------*/
	.node-container {
		border-radius: 6px !important;
		/* 带背景色块的边框圆角半径 */
	}
	/*↑-------@色块圆角------↑*/
	/*------------------------------------------------------------------------------------------------------------------*/
	/*-------@页面关系图------*/
	._3PC7s {
		/*页关系图样式包括宽度，背景色等*/
		width: 800px;
		height: 800px;
		border-radius: 6px;
	}
	/*↑-------@页面关系图------↑*/
	/*------------------------------------------------------------------------------------------------------------------*/
	/*-------右下角帮助------*/
	.QP3W5 path {
		/*右下角帮助中的按钮图标颜色*/
		fill: var(--wolai-svg-CF5659);
	}
	/*↑-------@右下角帮助------↑*/
	/*------------------------------------------------------------------------------------------------------------------*/
	/*-------@小号字体------*/
	.smallText {
		font-size: 16px !important;
	}
	/*↑-------强制小字体使用大字号------↑*/
	/*------------------------------------------------------------------------------------------------------------------*/
	/*-------@文字层级线------*/
	.node-container div[data-type="subnode"] {
		position: relative;
	}
	.node-container div[data-type="subnode"]:before {
		content: '';
		position: absolute;
		left: -14px;
		/* 文字层级标识线左右位置 */
		top: -5px;
		border-left: 2px solid rgba(150, 150, 150, 0.1);
		/* 文字层级标识线粗细颜色 */
		height: 100%;
	}
	/*↑-------文字层级线-------↑*/
	/*------------------------------------------------------------------------------------------------------------------*/
	/*-------@块文字居中居右------*/
	div[style*="color: rgb(241, 79, 176)"] {
		/*黑色模式下块菜单中粉色文字按钮改成文字居右对齐*/
		color: var(--wolai-caret-color) !important;
		letter-spacing: 1px;
		width: 100%;
		text-align: right;
	}
	div[style*="color: rgb(173, 26, 114)"] {
		/*白色模式下块菜单中粉色文字按钮改成文字居右对齐*/
		color: var(--wolai-caret-color) !important;
		letter-spacing: 1px;
		width: 100%;
		text-align: right;
	}
	div[style*="color: rgb(184, 107, 241)"] {
		/*暗色模式块菜单中紫色文字按钮改成文字居中对齐*/
		color: var(--wolai-caret-color) !important;
		letter-spacing: 1px;
		width: 100%;
		text-align: center;
	}
	div[style*="color: rgb(105, 64, 165)"] {
		/*白色模式块菜单中紫色文字按钮改成文字居中对齐*/
		color: var(--wolai-caret-color) !important;
		letter-spacing: 1px;
		width: 100%;
		text-align: center;
	}
	div[style="color: rgb(184, 107, 241); background: rgb(37, 37, 40);"] + div {
		font-size: 0;
	}
	div[style="color: rgb(184, 107, 241); background: rgb(37, 37, 40);"] + div:before {
		content: '居中';
		/*暗色模式块菜单中紫色文字按钮改成文字居中对齐*/
		visibility: visible;
		display: block;
		font-size: 12px;
	}
	.text-action-bar-color div[style="color: rgb(184, 107, 241); background: rgb(37, 37, 40);"] + div {
		font-size: 12px;
	}
	.text-action-bar-color div[style="color: rgb(184, 107, 241); background: rgb(37, 37, 40);"] + div:before {
		font-size: 0px;
	}
	div[style="color: rgb(241, 79, 176); background: rgb(37, 37, 40);"] + div {
		font-size: 0;
	}
	div[style="color: rgb(241, 79, 176); background: rgb(37, 37, 40);"] + div:before {
		content: '居右';
		/*暗色模式下块菜单中粉色文字按钮改成文字居右对齐*/
		visibility: visible;
		display: block;
		font-size: 12px;
	}
	.text-action-bar-color div[style="color: rgb(241, 79, 176); background: rgb(37, 37, 40);"] + div {
		font-size: 12px;
	}
	.text-action-bar-color div[style="color: rgb(241, 79, 176); background: rgb(37, 37, 40);"] + div:before {
		font-size: 0px;
	}
	div[style="color: rgb(105, 64, 165); background: rgb(255, 255, 255);"] + div {
		font-size: 0;
	}
	div[style="color: rgb(105, 64, 165); background: rgb(255, 255, 255);"] + div:before {
		content: '居中';
		/*白色模式块菜单中紫色文字按钮改成文字居中对齐*/
		visibility: visible;
		display: block;
		font-size: 12px;
	}
	.text-action-bar-color div[style="color: rgb(105, 64, 165); background: rgb(255, 255, 255);"] + div {
		font-size: 12px;
	}
	.text-action-bar-color div[style="color: rgb(105, 64, 165); background: rgb(255, 255, 255);"] + div:before {
		font-size: 0;
	}
	div[style="color: rgb(173, 26, 114); background: rgb(255, 255, 255);"] + div {
		font-size: 0;
	}
	div[style="color: rgb(173, 26, 114); background: rgb(255, 255, 255);"] + div:before {
		content: '居右';
		/*白色模式块菜单中粉色按钮改成文字居右对齐*/
		visibility: visible;
		display: block;
		font-size: 12px;
	}
	.text-action-bar-color div[style="color: rgb(173, 26, 114); background: rgb(255, 255, 255);"] + div {
		font-size: 12px;
	}
	.text-action-bar-color div[style="color: rgb(173, 26, 114); background: rgb(255, 255, 255);"] + div:before {
		font-size: 0;
	}
	/*↑-------块文字居中居右------↑*/
	/*------------------------------------------------------------------------------------------------------------------*/
	/*-------@图片------*/
	._2hMpN {
		text-align: center;
		/*设置图片说明文字水平居中*/
	}
	._2QRuO {
		border-radius: 6px;
		/*设置图片圆角半径*/
	}
	/*↑-------图片------↑*/
	/*------------------------------------------------------------------------------------------------------------------*/
	/*-------腾讯视频播放器-----*/
	div [data-block-type="tencentVideo"] ._3PQBd {
		border-radius: 9px;
		border: 2px solid rgb(200, 200, 200, 0.2) !important;
	}
	/*↑-------腾讯视频播放器------↑*/
	/*------------------------------------------------------------------------------------------------------------------*/
	/*-------@引述------*/
	._3Z5FW {
		width: 0px;
		/*引述文字左边竖线条宽度*/
	}
	html .W_9-O {
		/*background-color:rgba(220,220,220,0.3);/*引述文字背景颜色*/
		font-size: 16px;
		/*引述文字字号*/
		letter-spacing: 1px;
		/*引述文字间距*/
		border-radius: 6px;
		/*引述文字背景边框圆角半径*/
		padding: 3px 1px !important;
		display: -webkit-box;
		display: flex;
		text-align: justify;
		text-align-last: left;
	}
	.W_9-O:before {
		/*引述文字加入双引号*/
		content: '“';
		font-size: 50px;
		color: rgba(200, 200, 200, 0.8);
		/*font-weight:bolder;*/
		align-self: flex-start !important;
		margin: -10px auto -30px -12px;
	}
	.W_9-O:after {
		/*引述文字加入双引号*/
		content: '”';
		font-size: 50px;
		/*font-weight:bold;*/
		color: rgba(200, 200, 200, 0.8);
		align-self: flex-end !important;
		margin: auto -6px -30px 0px;
	}
	/*↑-------引述------↑*/
	/*------------------------------------------------------------------------------------------------------------------*/
	/*-------@着重------*/
	.node-container div[data-type="highlight"] {
		border-radius: 6px !important;
	}
	div[data-type="highlight"][style*="1px"] {
		border: 1px dashed #818182 !important;
		background-color: transparent !important;
	}
	div[style*="background: rgb(64, 66, 64)"] {
		background-color: #677A6E !important;
	}
	div[style*="background: rgb(67, 63, 60)"] {
		background-color: #7C6B5C !important;
	}
	div[style*="background: rgb(67, 61, 58)"] {
		background-color: #80614D !important;
	}
	div[style*="background: rgb(68, 67, 62)"] {
		background-color: #7E7B5F !important;
	}
	div[style*="background: rgb(61, 64, 67)"] {
		background-color: #556F7D !important;
	}
	div[style*="background: rgb(62, 62, 67)"] {
		background-color: #5F6580 !important;
	}
	div[style*="background: rgb(66, 64, 67)"] {
		background-color: #786D7E !important;
	}
	div[style*="background: rgb(65, 62, 64)"] {
		background-color: #75656E !important;
	}
	div[style*="background: rgb(67, 62, 62)"] {
		background-color: #7F6364 !important;
	}
	div[style*="background: rgb(67, 65, 65)"] {
		background-color: #151515 !important;
	}
	div[style*="background: rgb(64, 63, 63)"] {
		background-color: #2B2A2B !important;
	}
	/* 以上是暗色背景下着重文字背景色自定义 */
	div[placeholder="着重文字"] {
		font-size: 16px;
		/* 着重文字字号 */
		letter-spacing: 1px;
	}
	div[style*="background: rgb(252, 251, 251)"] {
		background-color: #F6F4F5 !important;
	}
	div[style*="background: rgb(247, 247, 247)"] {
		background-color: #ECE9EB !important;
	}
	div[style*="background: rgb(254, 247, 241)"] {
		background-color: #FBEADB !important;
	}
	div[style*="background: rgb(255, 243, 235)"] {
		background-color: #FFE0CC !important;
	}
	div[style*="background: rgb(255, 254, 242)"] {
		background-color: #FDFADE !important;
	}
	div[style*="background: rgb(246, 253, 248)"] {
		background-color: #E6F9ED !important;
	}
	div[style*="background: rgb(238, 249, 255)"] {
		background-color: #D4EEFC !important;
	}
	div[style*="background: rgb(242, 245, 255)"] {
		background-color: #DEE4FF !important;
	}
	div[style*="background: rgb(252, 248, 255)"] {
		background-color: #F7ECFD !important;
	}
	div[style*="background: rgb(252, 248, 255)"] {
		background-color: #F4E4ED !important;
	}
	div[style*="background: rgb(255, 244, 245)"] {
		background-color: #FEE2E3 !important;
	}
	/*↑-------着重------↑*/
	/*------------------------------------------------------------------------------------------------------------------*/
	/*-------@模板按钮------*/
	._3fusM:hover ._26KFI:hover {
		background-color: rgba(200, 199, 199, 0.2);
		/*模板按钮鼠标滑动上面底色*/
	}
	._3fusM ._2wdS- {
		border-radius: 9px;
		/*模板按钮底色边框圆角*/
	}
	._3fusM ._2wdS- ._2zCCk {
		/*模板按钮圆角*/
		border: 2px solid rgba(158, 156, 156, 0.55);
		border-radius: 6px;
		padding: 3px 6px;
	}
	.EG72n svg {
		width: 25px;
		height: 25px;
		padding: 0px 1px 0px 0px;
	}
	.EG72n path {
		/*按钮图标加号大小和颜色*/
		stroke-width: 2px;
		stroke: rgba(158, 156, 156, 0.55);
		fill: rgba(158, 156, 156, 0.55);
	}
	._3fusM ._2wdS- ._3--Y7 {
		padding: 12px 1px 0px 0px;
		/*按钮右侧笔型符号的位置*/
	}
	._2tnug {
		width: 20px;
		height: 25px;
		padding: 3px 1px 0px 0px;
	}
	._2tnug path {
		/*按钮图标记号大小和颜色*/
		stroke-width: 2px;
		stroke: rgba(175, 175, 175, 0.78);
		fill: rgba(175, 175, 175, 0.78);
	}
	/*↑-------模板按钮------↑*/
	/*------------------------------------------------------------------------------------------------------------------*/
	/*-------@代码片段------*/
	._2TL9K {
		font-size: 16px;
		font-family: 'Consolas'!important;
		/*代码片段字体和大小*/
		background-color: transparent;
	}
	._2ms-i {
		border-radius: 6px;
		border: 2px solid var(--wolai-border-color);
		background: var(--wolai-code-node-bg) !important;
		min-height: 42px;
		padding: 15px;
	}
	/*↑-------代码片段------↑*/
	/*------------------------------------------------------------------------------------------------------------------*/
	/*-------@附件------*/
