	/*↓-------@标题目录------↓*/
	div[placeholder="主标题"],
	div[placeholder="大标题"],
	div[placeholder="中标题"],
	div[placeholder="小标题"] {
		opacity: 0.9;
		transition-duration: 2s;
	}
	div[placeholder="主标题"]:after {
		content: "#";
		font-size: 34px !important;
	}
	div[placeholder="大标题"]:after {
		content: "##";
		font-size: 27px !important;
	}
	div[placeholder="中标题"]:after {
		content: "###";
		font-size: 20px !important;
	}
	div[placeholder="小标题"]:after {
		content: "####";
		font-size: 16px !important;
	}
	div[placeholder="主标题"]:after,
	div[placeholder="大标题"]:after,
	div[placeholder="中标题"]:after,
	div[placeholder="小标题"]:after {
		/*标题after样式公用样式*/
		width: 20px;
		font-weight: 600;
		border-radius: 20px;
		border: 0px dotted #ff0018 !important;
		color: rgba(204, 204, 204, .6) !important;
		background-color: transparent !important;
		margin: 0px !important;
		padding: 0px 3px !important;
		visibility: hidden;
	}
	div[placeholder="主标题"]:hover:after,
	div[placeholder="大标题"]:hover:after,
	div[placeholder="中标题"]:hover:after,
	div[placeholder="小标题"]:hover:after {
		visibility: visible;
		opacity: 1;
	}
	span[data-inline-type*="FontAwesome"] {
		margin: 0px 5px 0px 2px !important;
	}
