/*行内页引用*/
 span[data-biblock-type="page"] {
     /* 行内页引用 */
     color: var(--wolai-caret-color) !important;
     background-color: rgba(200, 200, 200, 0.2) !important;
     padding: 0px 6px 1px 8px !important;
     margin: 1px 4px 1px 6px;
     border-radius: 12px;
     cursor: pointer;
     font-size: 1em !important;
      position: relative;
     display: inline-block;
        /* 设置底部的边框 */
        border-bottom: 0px dashed black;
}
span[data-biblock-type="page"]:hover {
     /* 行内页引用 */
     color: #f5f5f5 !important;
     background: #cf5659 !important;
}
span[data-biblock-type="page"]:after {
     /* 行内页引用 */
     content:"頁";
     font-size: 10px !important;
     font-weight: 800;
     border-radius: 12px;
     border: 0px dotted #ff0018 !important;
     color: #cf5659;
     background-color: var(--wolai-editor-bg) !important;
     margin: 0 -3px 0 4px !important;
     padding: 1px 4px !important;
}

span[data-biblock-type="page"]:before {
     /*引用的小数字标hover提示引用数*/
     content:'这是一个引用页面';
     font-size:10px;
        visibility: hidden;
        width: 140px;
        background-color: #555;
        color: #eaeaea;
        text-align: center;
        border-radius: 6px;
        padding: 3px 5px;
        position: absolute;
        /* 设置该元素在z轴处于优先显示的位置，因为其他元素没有设置z—index */
        z-index: 1;
        /* 超过了100%就是反向距离 */
       bottom: 125%;
        left: 50%;
        margin-left: -60px;
        opacity: 0;
        /* 延迟一秒再变为清晰度是1 */
        transition: opacity 300ms;     
}

span[data-biblock-type="page"]:hover:before {
        visibility: visible;
        opacity: 1;
}

