css:
@-moz-document domain("horsereality.com")
{
/* BACKGROUND */
body.background.north-america, body.background.south-america, body.background.europe, body.background.australia, body.background.asia, body.background.africa
{
	background-image: url("https://i.imgur.com/fTpmPe7.png");
  	background-size: cover;
  	background-position: 0%;
}
}

@-moz-document url-prefix("https://www.horsereality.com") /* V1 */
{
/* V1 FLASH SALE */
.header-cta 
{
    background-color: #D7DDCF;
}
.header-cta h2 
{
    color: #869970;
}

/* V1 MENU */
.bluebar_container
{
    background-color: #505D42;
}
#bbmenu.user ul.slidedown li ul.slidedownmenu
{
    background-color: #505D42;
}
.leftnav>ul>li>ul>li a:active, .leftnav>ul>li>ul>li a:link, .leftnav>ul>li>ul>li a:visited
{
    color: #869970;
}

/* V1 HORSE MENU */
.block_tabclick:hover, .block_tabclick.tabsel
{
    background-color: #F5F7F3;
}
.undefined2 table
{
    background-color: #505D42;
    border-bottom: 3px solid #505D42;
    border-right: 1px solid #505D42;
    border-left: 1px solid #505D42;
}
table thead tr.top 
{
    background-color: #505D42;
}
table thead tr.sub 
{
    background-color: #869970;
}
table tr.odd 
{
    background-color: #F5F7F3;
}
table tr td a:active, table tr td a:link, table tr td a:visited 
{
    color: #869970;
}

/* V1 HORSE PAGE */
.leftnav /* NAV COLOUR */
{
    background-color: #F5F7F3;
}
.container /* NAV COLOUR */
{
    background-color: #F5F7F3;
}
p a:active, p a:link, p a:visited 
{
    color: #869970;
}
.horse_left .right a:active, .horse_left .right a:link, .horse_left .right a:visited /* MENU ACTION */
{
    color: #869970;
}
.tabclick, .tabclick2, .tabclick3 
{
    background-color: #F5F7F3;
}
.top /* PEDIGREE TAB */
{
    background-color: #505D42;

}
.horse_blockimg 
{
    background-color: transparent;
}
.container_12 .grid_8.training_left /* TRAINING TAB */
{

    border-left: 1px solid #505D42;
    border-bottom: 5px solid #505D42;
    border-right: 1px solid #505D42;
}
.training_left h6 
{
    background-color: #869970;
}
.trainbar>.exp 
{
    background: -webkit-gradient(linear,0% 0%,0% 100%,from(#A6B47E),to(#8A9A5B));
}
.training_right>.block>.barcon>.trainbar>.trained 
{
    background: -webkit-gradient(linear,0% 0%,0% 100%,from(#A6B47E),to(#8A9A5B));
}
.traincon.selected
{
    background-color: #F5F7F3!important;
}
.training_left .horsetraining .traincon 
{
    background-color: transparent;
}
.training_right>.block>.comp img, .training_right>.block>.show img 
{
    background-color: transparent;
}
.horse_blockimg 
{
    background-color: transparent;
}
.grid_6.genetics /* GENETIC TAB */
{
    border-left: 1px solid #505D42;
    border-bottom: 5px solid #505D42;
    border-right: 1px solid #505D42;
}
.subtop 
{
    background-color: #869970;
}
.genetic_result 
{
    background-color: #F5F7F3;
}
.genetic_tests .test_block .first 
{
    background-color: #F5F7F3;
}
.genetic_potential
{
    background-color: #F5F7F3;
}
.genetic_table_row 
{
    border-bottom: none;
}
.container_12 .grid_4.training_right /* ACHIEVEMENTS TAB */
{
    border-left: 1px solid #505D42;
    border-bottom: 5px solid #505D42;
    border-right: 1px solid #505D42;
}
.odd
{
    background-color: #F5F7F3;
}
.container_12 .grid_12.stable_block /* OFFSPRING TAB */
{
    border-left: 1px solid #505D42;
    border-bottom: 5px solid #505D42;
    border-right: 1px solid #505D42;
}
.col_200 a:active, .col_200 a:link, .col_200 a:visited 
{
    color: #869970;
}
.col_150 a:active, .col_150 a:link, .col_150 a:visited 
{
    color: #869970;
}
.container_12 .grid_6.half_block  /* HEALTH TAB */
{
    border-left: 1px solid #505D42;
    border-bottom: 5px solid #505D42;
    border-right: 1px solid #505D42;
}

/* V1 BUTTON */
button.yellow,input[type=submit].yellow,button.dark, input[type=submit].dark,button.yellow:hover,input[type=submit].yellow:hover,button.dark:hover,input[type=submit].dark:hover,button.yellow:active,input[type=submit].yellow:active,button.dark:active,input[type=submit].dark:active
{
    background-color:#869970;
    border: none;
    box-shadow: none;
    outline: none;
}

/* V1 HORSE BREEDING */
.grid_12.breeding 
{
    border-left: 1px solid #505D42;
    border-bottom: 5px solid #505D42;
    border-right: 1px solid #505D42;
}

/* V1 LABORATORY */
.lab_selectall 
{
    background-color: #869970;
}

/* V1 INVENTORY */
.breadcrumbs 
{
    background-color: #F5F7F3;
}
.storeblocks, .horseblocks 
{
    background-color: #F5F7F3;
}
#store_left.tabnav .store_tabclick:hover, #store_left.tabnav .store_tabclick.tabsel 
{
    background-color: #F5F7F3;
}
}

@-moz-document url-prefix("https://v2.horsereality.com") /* V2 */
{
/* V2 FLASH SALE */
.header-cta 
{
    background-color: #D7DDCF;
}
.header-cta h2 
{
    color: #869970;
}

/* V2 MENU */
.header-menu nav .main-menu .user-nav .dropdown-menu .dropdown-item:hover /* LINK COLOUR */
{
  color: #CAD2B2;
}
.header-menu nav .main-menu ul li a:not(.btn):hover
{  
  color: #CAD2B2;
}
.header-menu nav .main-menu .user-nav>a:hover
{
  color: #CAD2B2;
}
.header-menu
{
    background: #505D42;
}
.side-nav
{
    background: #F5F7F3;
}
.side-nav ul a 
{
    color: #869970;
}
.header-menu nav .main-menu .user-nav .dropdown-menu
{
    background: #505D42;
}
.mobile-menu-container
{
    background: #505D42;
}
.progress-bar
{
    background-color: #8A9A5B;
}

/* V2 PROFILE */
.show-profile .navbar /* PROFILE TAB */
{
    background-color: #505d42!important;
    border-top: 2px solid #505d42;
    border-bottom: 2px solid #505d42;
}
.show-profile .profile-account
{
    background-color: #F5F7F3;
}
.show-profile .navbar-avatar .navbar-avatar-container
{
    border: 2px solid #505D42;
}
.component.frame .wrapper /* HORSES TAB */
{
    background: #505D42;
}
.component.table .table thead th
{
    background: #809469;
}
td.horse-name-cell a
{
    color: #869970;
}
.component.table .table tbody tr:nth-of-type(odd) 
{
    background-color: #F5F7F3;
}
.sales-table-row .odd-row /* SALES TAB */
{
    background-color: #F5F7F3;
}
.sales .horse-name 
{
    font-weight: 600;
    color: #869970;
}
.topic-title a /* FORUM TAB */
{
    color: #869970;
}
.footer 
{
    background: #505D42;
}

/* V2 ESTATE */
.estate-item 
{
    background-color: #F5F7F3;
}
.estate-item .estate-item-buy button,.estate-item .estate-item-buy button:hover
{
    background-color: #8A9A5B;
}

/* V2 NOTIFICATION */
a
{
    color: #869970;
}
nav .pagination .page-item.active .page-link 
{
    background: #869970;
}
nav .pagination .page-item.active .page-link:hover 
{
    background: #869970;
    color: white;
}
nav .pagination .page-item .page-link:hover
{
   background: #F5F7F3;
}

/* V2 BUTTON */
.btn.btn-cta,.btn.btn-primary,.btn-warning,#billingTypeButton,.btn.btn-warning.btn-cta.btn-sm,.btn.btn-primary.btn-darkblue.btn-sm,.btn.btn-cta.btn-warning.btn-sm.single-add,.btn.btn-cta.btn-warning.btn-sm.multi-add,.btn.btn-warning.btn-cta.inline,.btn.btn-primary.mb-3,.btn.btn-warning.btn-cta,.header-cta button,.btn.btn-cta:hover,.btn.btn-primary:hover,.btn-warning:hover,#billingTypeButton:hover,.btn.btn-warning.btn-cta.btn-sm:hover,.btn.btn-primary.btn-darkblue.btn-sm:hover,.btn.btn-cta.btn-warning.btn-sm.single-add:hover,.btn.btn-cta.btn-warning.btn-sm.multi-add:hover,.btn.btn-warning.btn-cta.inline:hover,.btn.btn-primary.mb-3:hover,.btn.btn-warning.btn-cta:hover,.header-cta button:hover,.btn.btn-cta:active,.btn.btn-primary:active,.btn-warning:active,#billingTypeButton:active,.btn.btn-warning.btn-cta.btn-sm:active,.btn.btn-primary.btn-darkblue.btn-sm:active,.btn.btn-cta.btn-warning.btn-sm.single-add:active,.btn.btn-cta.btn-warning.btn-sm.multi-add:active,.btn.btn-warning.btn-cta.inline:active,.btn.btn-primary.mb-3:active,.btn.btn-warning.btn-cta:active,.header-cta button:active
{
    background-color: #869970;
    border-color: #869970;
    color: #ffffff;
    border: none;
    box-shadow: none;
    outline: none;
}

/* V2 FITTING ROOM */
.fitting-room .horse-select-row 
{
    background-color: #505D42;
}
.fitting-room .selectedRow 
{
    background-color: #D7DDCF!important;
}
.fitting-room .fitting-room-bg-row:hover
{
   background-color:#D7DDCF!important;
}
.fitting-room .buy-row .buy-row-left .go-to-inventory-btn 
{
    background-color: #869970;
    border: 1px solid #869970;
}

/* V2 FOAL PASTURE */
.component.npc 
{
    background: #F5F7F3;
}
.card-item 
{
    background-color: #F5F7F3;
}
.card-item-action button.black 
{
    background-color: #869970;
}
.card-item-action button.black:hover 
{
    background-color: #869970;
}
.card-item-action button 
{
    background-color: #869970;
}
.card-item-action button:hover 
{
    background-color: #869970;
}
.form-control:disabled, .form-control[readonly] 
{
    background-color: #ffff;;
}

/* V2 FREEZER */
p a:active, p a:link, p a:visited 
{
    color: #869970;
}

/* V2 COMPETITIONS */
.breadcrumb 
{
    background-color: #F5F7F3;
}

/* V2 NEWS */
.component.frame .wrapper .subheader
{
    background-color: #869970;
}
.component.comments .comment .header
{
    background-color: #869970;
}
.component.comments .comment .profile-wrapper 
{
    background-color: #F5F7F3;
}
.breadcrumb-item.active 
{
    color: #869970;
}
nav .pagination .page-item.active .page-link:hover
{
    background: #869970;
    color: white;
}
nav .pagination .page-item .page-link 
{
    background: transparent;
}
.component.comments .comment .profile-wrapper .profile .avatar 
{
    border: 2px solid #869970;
}
}
