@-moz-document domain("wolai.com") {
/*------------------------------------------------------------------------------------------------------------------*/
	/*-------@文字批注------*/
	:root[theme='dark'] {
		--wolaistyleplus-text-comments-notes-bgcolor: #f4a35f;
		/*文字批注 注释背景色*/
		--wolaistyleplus-text-comments-highlight-bgcolor: #f6cc33;
		/*文字批注 高亮背景色*/
		--wolaistyleplus-text-comments-revised-bgcolor: #bbc61c;
		/*文字批注 修改背景色*/
		--wolaistyleplus-text-comments-delete-bgcolor: #89d3f9;
		/*文字批注 删除背景色*/
		--wolaistyleplus-text-comments-supplement-bgcolor: #97f3d9;
		/*文字批注 补充背景色*/
		--wolaistyleplus-text-comments-comments-bgcolor: #cb99fc;
		/*文字批注 评语背景色*/
		--wolaistyleplus-text-comments-appreciation-bgcolor: #ffcfd1;
		/*文字批注 赏析背景色*/
		--wolaistyleplus-text-comments-doubt-bgcolor: #f78a85;
		/*文字批注 疑问背景色*/
		/*-----------------------------------------------*/
		--wolaistyleplus-text-comments-dot-color: red;
		/*文字批注 圆点颜色*/
		--wolaistyleplus-text-comments-dot-bgcolor: rgba(200, 200, 200, 0.2);
		/*文字批注 圆点背景色*/
		--wolaistyleplus-text-comments-wave-color: red;
		/*文字批注 波浪颜色*/
		--wolaistyleplus-text-comments-wave-bgcolor: rgba(200, 200, 200, 0.2);
		/*文字批注 波浪背景色*/
		--wolaistyleplus-text-comments-triangle-color: red;
		/*文字批注 三角颜色*/
		--wolaistyleplus-text-comments-triangle-bgcolor: rgba(200, 200, 200, 0.2);
		/*文字批注 三角背景色*/
	}
	:root {
		--wolaistyleplus-text-comments-notes-bgcolor: #f4a35f;
		/*文字批注 注释背景色*/
		--wolaistyleplus-text-comments-highlight-bgcolor: #f6cc33;
		/*文字批注 高亮背景色*/
		--wolaistyleplus-text-comments-revised-bgcolor: #bbc61c;
		/*文字批注 修改背景色*/
		--wolaistyleplus-text-comments-delete-bgcolor: #89d3f9;
		/*文字批注 删除背景色*/
		--wolaistyleplus-text-comments-supplement-bgcolor: #97f3d9;
		/*文字批注 补充背景色*/
		--wolaistyleplus-text-comments-comments-bgcolor: #cb99fc;
		/*文字批注 评语背景色*/
		--wolaistyleplus-text-comments-appreciation-bgcolor: #ffcfd1;
		/*文字批注 赏析背景色*/
		--wolaistyleplus-text-comments-doubt-bgcolor: #f78a85;
		/*文字批注 疑问背景色*/
		/*-----------------------------------------------*/
		--wolaistyleplus-text-comments-dot-color: red;
		/*文字批注 圆点颜色*/
		--wolaistyleplus-text-comments-dot-bgcolor: rgba(200, 200, 200, 0.2);
		/*文字批注 圆点背景色*/
		--wolaistyleplus-text-comments-wave-color: red;
		/*文字批注 波浪颜色*/
		--wolaistyleplus-text-comments-wave-bgcolor: rgba(200, 200, 200, 0.2);
		/*文字批注 波浪背景色*/
		--wolaistyleplus-text-comments-triangle-color: red;
		/*文字批注 三角颜色*/
		--wolaistyleplus-text-comments-triangle-bgcolor: rgba(200, 200, 200, 0.2);
		/*文字批注 三角背景色*/
	}

	div [style*="background-color:rgba(237, 233, 235, 0.09)"],
	div [style*="background-color:rgba(237, 233, 235, 0.5)"],
	div [style*="background-color:rgba(247, 214, 183, 0.5)"] {
		/*三角符号，圆点符号的一些公共样式*/
		font-family: 'Source Code Pro', 'Consolas', 'Monaco', 'monospace' !important;
		border-radius: 20px;
		padding: 3px 8px 3px 8px;
		-webkit-text-emphasis-position: under right;
		text-emphasis-position: under right;
		letter-spacing: 0px !important;
	}
	div [style*="background-color:rgba(237, 233, 235, 0.09)"] {
		/*三角符号线 暗色主题白灰背景按钮*/
		-webkit-text-emphasis: filled triangle;
		text-emphasis: filled triangle;
		background-color: var(--wolaistyleplus-text-comments-triangle-bgcolor) !important;
		text-emphasis-color: var(--wolaistyleplus-text-comments-triangle-color) !important;
		-webkit-text-emphasis-color: var(--wolaistyleplus-text-comments-triangle-color) !important;
	}
	div [style*="background-color:rgba(237, 233, 235, 0.5)"] {
		/*三角符号线 白色主题白灰背景按钮*/
		-webkit-text-emphasis: filled triangle;
		text-emphasis: filled triangle;
		background-color: var(--wolaistyleplus-text-comments-triangle-bgcolor) !important;
		text-emphasis-color: var(--wolaistyleplus-text-comments-triangle-color) !important;
		-webkit-text-emphasis-color: var(--wolaistyleplus-text-comments-triangle-color) !important;
	}
	div [style*="background-color:rgba(247, 214, 183, 0.5)"] {
		/*圆点符号 幼杏背景按钮 */
		-webkit-text-emphasis: filled circle;
		text-emphasis: filled circle;
		background-color: var(--wolaistyleplus-text-comments-dot-bgcolor) !important;
		text-emphasis-color: var(--wolaistyleplus-text-comments-dot-color) !important;
		-webkit-text-emphasis-color: var(--wolaistyleplus-text-comments-dot-color) !important;
	}
	div [style*="background-color:rgba(217, 211, 215, 0.2)"],
	div [style*="background-color:rgba(217, 211, 215, 0.5)"] {
		/*波浪线符号 暗银背景按钮 */
		font-family: 'Source Code Pro', 'Consolas', 'Monaco', 'monospace' !important;
		background-color: var(--wolaistyleplus-text-comments-wave-bgcolor) !important;
		border-radius: 20px;
		padding: 2px 8px 3px 8px;
		text-decoration: underline wavy var(--wolaistyleplus-text-comments-wave-color);
		line-height: 32px;
	}
	div [style*="background-color:rgba(169, 222, 249, 0.5)"],
	div [style*="background-color:rgba(205, 243, 220, 0.5)"],
	div [style*="background-color:rgba(255, 193, 153, 0.5)"],
	div [style*="background-color:rgba(252, 246, 189, 0.5)"],
	div [style*="background-color:rgba(239, 218, 251, 0.5)"],
	div [style*="background-color:rgba(253, 198, 200, 0.5)"],
	div [style*="background-color:rgba(189, 201, 255, 0.5)"],
	div [style*="background-color:rgba(234, 202, 220, 0.5)"] {
		/*八种批注的公共样式*/
		border-radius: 20px;
		padding: 0px 5px 2px 8px !important;
		margin: 0px 2px !important;
		letter-spacing: 0px !important;
		color: #000000 !important;
	}
	div [style*="background-color:rgba(169, 222, 249, 0.5)"]:after,
	div [style*="background-color:rgba(205, 243, 220, 0.5)"]:after,
	div [style*="background-color:rgba(255, 193, 153, 0.5)"]:after,
	div [style*="background-color:rgba(252, 246, 189, 0.5)"]:after,
	div [style*="background-color:rgba(239, 218, 251, 0.5)"]:after,
	div [style*="background-color:rgba(253, 198, 200, 0.5)"]:after,
	div [style*="background-color:rgba(189, 201, 255, 0.5)"]:after,
	div [style*="background-color:rgba(234, 202, 220, 0.5)"]:after {
		/*八种批注文字标的公共样式*/
		width: 20px;
		font-size: 12px;
		font-weight: 700;
		border-radius: 20px;
		margin: 0px -2px 4px 3px;
		padding: 1px 3px;
		background-color: var(--wolai-editor-bg) !important;
	}
	div [style*="background-color:rgba(169, 222, 249, 0.5)"] {
		/*删除 天藍背景按钮*/
		background-color: var(--wolaistyleplus-text-comments-delete-bgcolor) !important;
	}
	div [style*="background-color:rgba(169, 222, 249, 0.5)"]:after {
		/*删除 天藍背景按钮*/
		content: "刪";
		color: var(--wolaistyleplus-text-comments-delete-bgcolor);
	}
	div [style*="background-color:rgba(205, 243, 220, 0.5)"] {
		/*修改 浅绿背景按钮 */
		background-color: var(--wolaistyleplus-text-comments-revised-bgcolor) !important;
	}
	div [style*="background-color:rgba(205, 243, 220, 0.5)"]:after {
		/*修改 浅绿背景按钮 */
		content: "改";
		color: var(--wolaistyleplus-text-comments-revised-bgcolor);
	}
	div [style*="background-color:rgba(255, 193, 153, 0.5)"] {
		/*注释 橘色背景按钮*/
		background-color: var(--wolaistyleplus-text-comments-notes-bgcolor) !important;
	}
	div [style*="background-color:rgba(255, 193, 153, 0.5)"]:after {
		/*注释 橘色背景按钮*/
		content: "注";
		color: var(--wolaistyleplus-text-comments-notes-bgcolor);
	}
	div [style*="background-color:rgba(252, 246, 189, 0.5)"] {
		/*高亮 淡黄背景按钮*/
		background-color: var(--wolaistyleplus-text-comments-highlight-bgcolor) !important;
	}
	div [style*="background-color:rgba(252, 246, 189, 0.5)"]:after {
		/*高亮 淡黄背景按钮*/
		content: "亮";
		color: var(--wolaistyleplus-text-comments-highlight-bgcolor);
	}
	div [style*="background-color:rgba(239, 218, 251, 0.5)"] {
		/*评语 轻紫背景按钮*/
		background-color: var(--wolaistyleplus-text-comments-comments-bgcolor) !important;
	}
	div [style*="background-color:rgba(239, 218, 251, 0.5)"]:after {
		/*评语 轻紫背景按钮*/
		content: "评";
		color: var(--wolaistyleplus-text-comments-comments-bgcolor) !important;
	}
	div [style*="background-color:rgba(253, 198, 200, 0.5)"] {
		/*疑问 将红背景按钮*/
		background-color: var(--wolaistyleplus-text-comments-doubt-bgcolor) !important;
	}
	div [style*="background-color:rgba(253, 198, 200, 0.5)"]:after {
		/*疑问 将红背景按钮*/
		content: "问";
		color: var(--wolaistyleplus-text-comments-doubt-bgcolor);
	}
	div [style*="background-color:rgba(189, 201, 255, 0.5)"] {
		/*补充 雾藍背景按钮*/
		background-color: var(--wolaistyleplus-text-comments-supplement-bgcolor) !important;
	}
	div [style*="background-color:rgba(189, 201, 255, 0.5)"]:after {
		/*补充 雾藍背景按钮*/
		content: "补";
		color: var(--wolaistyleplus-text-comments-supplement-bgcolor);
	}
	div [style*="background-color:rgba(234, 202, 220, 0.5)"] {
		/*赏析 熏粉背景按钮*/
		background-color: var(--wolaistyleplus-text-comments-appreciation-bgcolor) !important;
	}
	div [style*="background-color:rgba(234, 202, 220, 0.5)"]:after {
		/*赏析 熏粉背景按钮*/
		content: "析";
		color: var(--wolaistyleplus-text-comments-appreciation-bgcolor);
	}
	/*以上是文字批注的样式*/
	/*以下是将菜单中背景色按钮文字改成对应的批注内容*/
	.text-action-bar-color div[style="background: rgba(237, 233, 235, 0.09);"]:before,
	.text-action-bar-color div[style="background: rgba(237, 233, 235, 0.5);"]:before,
	.text-action-bar-color div[style="background: rgba(217, 211, 215, 0.2);"]:before,
	.text-action-bar-color div[style="background: rgba(217, 211, 215, 0.5);"]:before,
	.text-action-bar-color div[style="background: rgba(247, 214, 183, 0.5);"]:before,
	.text-action-bar-color div[style="background: rgba(234, 202, 220, 0.5);"]:before,
	.text-action-bar-color div[style="background: rgba(255, 193, 153, 0.5);"]:before,
	.text-action-bar-color div[style="background: rgba(252, 246, 189, 0.5);"]:before,
	.text-action-bar-color div[style="background: rgba(205, 243, 220, 0.5);"]:before,
	.text-action-bar-color div[style="background: rgba(169, 222, 249, 0.5);"]:before,
	.text-action-bar-color div[style="background: rgba(239, 218, 251, 0.5);"]:before,
	.text-action-bar-color div[style="background: rgba(253, 198, 200, 0.5);"]:before,
	.text-action-bar-color div[style="background: rgba(189, 201, 255, 0.5);"]:before {
		visibility: visible;
		display: block;
		font-size: 14px;
		color: #000;
	}
	.text-action-bar-color div[style="background: rgba(237, 233, 235, 0.09);"] + div:before,
	.text-action-bar-color div[style="background: rgba(237, 233, 235, 0.5);"] + div:before,
	.text-action-bar-color div[style="background: rgba(217, 211, 215, 0.2);"] + div:before,
	.text-action-bar-color div[style="background: rgba(217, 211, 215, 0.5);"] + div:before,
	.text-action-bar-color div[style="background: rgba(247, 214, 183, 0.5);"] + div:before,
	.text-action-bar-color div[style="background: rgba(234, 202, 220, 0.5);"] + div:before,
	.text-action-bar-color div[style="background: rgba(255, 193, 153, 0.5);"] + div:before,
	.text-action-bar-color div[style="background: rgba(252, 246, 189, 0.5);"] + div:before,
	.text-action-bar-color div[style="background: rgba(205, 243, 220, 0.5);"] + div:before,
	.text-action-bar-color div[style="background: rgba(169, 222, 249, 0.5);"] + div:before,
	.text-action-bar-color div[style="background: rgba(239, 218, 251, 0.5);"] + div:before,
	.text-action-bar-color div[style="background: rgba(253, 198, 200, 0.5);"] + div:before,
	.text-action-bar-color div[style="background: rgba(189, 201, 255, 0.5);"] + div:before {
		visibility: visible;
		display: block;
		font-size: 12px;
	}
	.text-action-bar-color div[style="background: rgba(237, 233, 235, 0.09);"] + div,
	.text-action-bar-color div[style="background: rgba(237, 233, 235, 0.5);"] + div,
	.text-action-bar-color div[style="background: rgba(217, 211, 215, 0.2);"] + div,
	.text-action-bar-color div[style="background: rgba(217, 211, 215, 0.5);"] + div,
	.text-action-bar-color div[style="background: rgba(247, 214, 183, 0.5);"] + div,
	.text-action-bar-color div[style="background: rgba(234, 202, 220, 0.5);"] + div,
	.text-action-bar-color div[style="background: rgba(255, 193, 153, 0.5);"] + div,
	.text-action-bar-color div[style="background: rgba(252, 246, 189, 0.5);"] + div,
	.text-action-bar-color div[style="background: rgba(205, 243, 220, 0.5);"] + div,
	.text-action-bar-color div[style="background: rgba(169, 222, 249, 0.5);"] + div,
	.text-action-bar-color div[style="background: rgba(239, 218, 251, 0.5);"] + div,
	.text-action-bar-color div[style="background: rgba(253, 198, 200, 0.5);"] + div,
	.text-action-bar-color div[style="background: rgba(189, 201, 255, 0.5);"] + div {
		font-size: 0;
	}
	.text-action-bar-color div[style="background: rgba(237, 233, 235, 0.09);"],
	.text-action-bar-color div[style="background: rgba(237, 233, 235, 0.5);"],
	.text-action-bar-color div[style="background: rgba(217, 211, 215, 0.2);"],
	.text-action-bar-color div[style="background: rgba(217, 211, 215, 0.5);"],
	.text-action-bar-color div[style="background: rgba(247, 214, 183, 0.5);"],
	.text-action-bar-color div[style="background: rgba(234, 202, 220, 0.5);"],
	.text-action-bar-color div[style="background: rgba(255, 193, 153, 0.5);"],
	.text-action-bar-color div[style="background: rgba(252, 246, 189, 0.5);"],
	.text-action-bar-color div[style="background: rgba(205, 243, 220, 0.5);"],
	.text-action-bar-color div[style="background: rgba(169, 222, 249, 0.5);"],
	.text-action-bar-color div[style="background: rgba(239, 218, 251, 0.5);"],
	.text-action-bar-color div[style="background: rgba(253, 198, 200, 0.5);"],
	.text-action-bar-color div[style="background: rgba(189, 201, 255, 0.5);"] {
		font-size: 0;
		border-radius: 11px;
	}
	.text-action-bar-color div[style="background: rgba(237, 233, 235, 0.09);"]:before,
	.text-action-bar-color div[style="background: rgba(237, 233, 235, 0.5);"]:before {
		/*白灰按钮*/
		content: '▴▴';
		color: var(--wolaistyleplus-text-comments-triangle-color);
	}
	.text-action-bar-color div[style="background: rgba(237, 233, 235, 0.09);"] + div:before,
	.text-action-bar-color div[style="background: rgba(237, 233, 235, 0.5);"] + div:before {
		/*白灰按钮*/
		content: '三角';
	}
	/*-------------------------------------*/
	.text-action-bar-color div[style="background: rgba(217, 211, 215, 0.2);"]:before,
	.text-action-bar-color div[style="background: rgba(217, 211, 215, 0.5);"]:before {
		/*暗银按钮*/
		content: '～';
		color: var(--wolaistyleplus-text-comments-wave-color);
	}
	.text-action-bar-color div[style="background: rgba(217, 211, 215, 0.2);"] + div:before,
	.text-action-bar-color div[style="background: rgba(217, 211, 215, 0.5);"] + div:before {
		/*暗银按钮*/
		content: '波浪';
	}
	/*-------------------------------------*/
	.text-action-bar-color div[style="background: rgba(247, 214, 183, 0.5);"]:before {
		/*幼杏按钮*/
		content: '• •';
		color: var(--wolaistyleplus-text-comments-dot-color);
	}
	.text-action-bar-color div[style="background: rgba(247, 214, 183, 0.5);"] + div:before {
		/*幼杏按钮*/
		content: '圆点';
	}
	/*-------------------------------------*/
	.text-action-bar-color div[style="background: rgba(234, 202, 220, 0.5);"]:before {
		/*熏粉按钮*/
		content: '析';
	}
	.text-action-bar-color div[style="background: rgba(234, 202, 220, 0.5);"] {
		background-color: var(--wolaistyleplus-text-comments-appreciation-bgcolor) !important;
	}
	.text-action-bar-color div[style="background: rgba(234, 202, 220, 0.5);"] + div:before {
		/*熏粉按钮*/
		content: '赏析';
	}
	/*-------------------------------------*/
	.text-action-bar-color div[style="background: rgba(255, 193, 153, 0.5);"]:before {
		/*橘色按钮*/
		content: '注';
	}
	.text-action-bar-color div[style="background: rgba(255, 193, 153, 0.5);"] {
		background-color: var(--wolaistyleplus-text-comments-notes-bgcolor) !important;
	}
	.text-action-bar-color div[style="background: rgba(255, 193, 153, 0.5);"] + div:before {
		/*橘色按钮*/
		content: '注释';
	}
	/*-------------------------------------*/
	.text-action-bar-color div[style="background: rgba(252, 246, 189, 0.5);"]:before {
		/*淡黄按钮*/
		content: '亮';
	}
	.text-action-bar-color div[style="background: rgba(252, 246, 189, 0.5);"] {
		background-color: var(--wolaistyleplus-text-comments-highlight-bgcolor) !important;
	}
	.text-action-bar-color div[style="background: rgba(252, 246, 189, 0.5);"] + div:before {
		/*淡黄按钮*/
		content: '高亮';
	}
	/*-------------------------------------*/
	.text-action-bar-color div[style="background: rgba(205, 243, 220, 0.5);"]:before {
		/*浅绿按钮*/
		content: '改';
	}
	.text-action-bar-color div[style="background: rgba(205, 243, 220, 0.5);"] {
		background-color: var(--wolaistyleplus-text-comments-revised-bgcolor) !important;
	}
	.text-action-bar-color div[style="background: rgba(205, 243, 220, 0.5);"] + div:before {
		/*浅绿按钮*/
		content: '修改';
	}
	/*-------------------------------------*/
	.text-action-bar-color div[style="background: rgba(169, 222, 249, 0.5);"]:before {
		/*天蓝按钮*/
		content: '删';
	}
	.text-action-bar-color div[style="background: rgba(169, 222, 249, 0.5);"] {
		background-color: var(--wolaistyleplus-text-comments-delete-bgcolor) !important;
	}
	.text-action-bar-color div[style="background: rgba(169, 222, 249, 0.5);"] + div:before {
		/*天蓝按钮*/
		content: '删除';
	}
	/*-------------------------------------*/
	.text-action-bar-color div[style="background: rgba(239, 218, 251, 0.5);"]:before {
		/*轻紫按钮*/
		content: '评';
	}
	.text-action-bar-color div[style="background: rgba(239, 218, 251, 0.5);"] {
		background-color: var(--wolaistyleplus-text-comments-comments-bgcolor) !important;
	}
	.text-action-bar-color div[style="background: rgba(239, 218, 251, 0.5);"] + div:before {
		/*轻紫按钮*/
		content: '评语';
	}
	/*-------------------------------------*/
	.text-action-bar-color div[style="background: rgba(253, 198, 200, 0.5);"]:before {
		/*将红按钮*/
		content: '问';
	}
	.text-action-bar-color div[style="background: rgba(253, 198, 200, 0.5);"] {
		background-color: var(--wolaistyleplus-text-comments-doubt-bgcolor) !important;
